// SPDX-FileCopyrightText: 2024 Toucan Protocol
//
// SPDX-License-Identifier: UNLICENSED

// If you encounter a vulnerability or an issue, please contact <info@neutralx.com>
pragma solidity ^0.8.13;

struct FeeDistribution {
    address[] recipients;
    uint256[] shares;
}

/// @title IFeeCalculator
/// @author Neutral Labs Inc.
/// @notice This interface defines methods for calculating fees.
interface IFeeCalculator {
    /// @notice Calculates the deposit fee for a given amount.
    /// @param pool The address of the pool.
    /// @param tco2 The address of the TCO2 token.
    /// @param depositAmount The amount to be deposited.
    /// @return feeDistribution How the fee is meant to be
    /// distributed among the fee recipients.
    function calculateDepositFees(address pool, address tco2, uint256 depositAmount)
        external
        view
        returns (FeeDistribution memory feeDistribution);

    /// @notice Calculates the redemption fees for a given amount.
    /// @param pool The address of the pool.
    /// @param tco2s The addresses of the TCO2 token.
    /// @param redemptionAmounts The amounts to be redeemed.
    /// @return feeDistribution How the fee is meant to be
    /// distributed among the fee recipients.
    function calculateRedemptionFees(address pool, address[] calldata tco2s, uint256[] calldata redemptionAmounts)
        external
        view
        returns (FeeDistribution memory feeDistribution);

    /// @notice Calculates the deposit fee for a given amount of an ERC1155 project.
    /// @param pool The address of the pool.
    /// @param erc1155 The address of the ERC1155 project
    /// @param tokenId The tokenId of the vintage.
    /// @param depositAmount The amount to be deposited.
    /// @return feeDistribution How the fee is meant to be
    /// distributed among the fee recipients.
    function calculateDepositFees(address pool, address erc1155, uint256 tokenId, uint256 depositAmount)
        external
        view
        returns (FeeDistribution memory feeDistribution);

    /// @notice Calculates the redemption fees for a given amount on ERC1155 projects.
    /// @param pool The address of the pool.
    /// @param erc1155s The addresses of the ERC1155 projects.
    /// @param tokenIds The tokenIds of the project vintages.
    /// @param redemptionAmounts The amounts to be redeemed.
    /// @return feeDistribution How the fee is meant to be
    /// distributed among the fee recipients.
    function calculateRedemptionFees(
        address pool,
        address[] calldata erc1155s,
        uint256[] calldata tokenIds,
        uint256[] calldata redemptionAmounts
    ) external view returns (FeeDistribution memory feeDistribution);
}

contracts/bases/RoleInitializer.sol.content:
// SPDX-FileCopyrightText: 2023 Toucan Labs
//
// SPDX-License-Identifier: UNLICENSED

// If you encounter a vulnerability or an issue, please contact <security@toucan.earth> or visit security.toucan.earth
pragma solidity 0.8.14;

import '@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol';

abstract contract RoleInitializer is AccessControlUpgradeable {
    function __RoleInitializer_init_unchained(
        address[] calldata accounts,
        bytes32[] calldata roles
    ) internal {
        require(accounts.length == roles.length, 'Array length mismatch');

        __AccessControl_init_unchained();

        bool hasDefaultAdmin = false;
        for (uint256 i = 0; i < accounts.length; ++i) {
            _grantRole(roles[i], accounts[i]);
            if (roles[i] == DEFAULT_ADMIN_ROLE) hasDefaultAdmin = true;
        }
        require(hasDefaultAdmin, 'No admin specified');
    }
}

contracts/bases/ToucanCarbonOffsetsBase..content:
// SPDX-FileCopyrightText: 2023 Toucan Labs
//
// SPDX-License-Identifier: UNLICENSED

// If you encounter a vulnerability or an issue, please contact <security@toucan.earth> or visit security.toucan.earth
pragma solidity 0.8.14;

import '@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol';
import '@openzeppelin/contracts/token/ERC721/IERC721.sol';
import '@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol';

import '../interfaces/ICarbonOffsetBatches.sol';
import '../interfaces/ICarbonProjects.sol';
import '../interfaces/ICarbonProjectVintages.sol';
import '../interfaces/IPausable.sol';
import '../interfaces/IToucanCarbonOffsetsEscrow.sol';
import '../interfaces/IToucanCarbonOffsetsFactory.sol';
import '../interfaces/IToucanContractRegistry.sol';
import '../retirements/interfaces/IRetirementCertificates.sol';
import '../ToucanCarbonOffsetsStorage.sol';
import {BatchStatus} from '../CarbonOffsetBatchesTypes.sol';

/// @notice Base contract for any specific contract implementation of the TCO2 tokens (ERC20)
abstract contract ToucanCarbonOffsetsBase is
    ERC20Upgradeable,
    ToucanCarbonOffsetsStorage
{
    // ----------------------------------------
    //              Constants
    // ----------------------------------------

    /// @dev All roles related to accessing this contract
    bytes32 public constant DETOKENIZER_ROLE = keccak256('DETOKENIZER_ROLE');
    bytes32 public constant TOKENIZER_ROLE = keccak256('TOKENIZER_ROLE');
    bytes32 public constant RETIREMENT_ROLE = keccak256('RETIREMENT_ROLE');

    // ----------------------------------------
    //      Events
    // ----------------------------------------

    event FeePaid(address bridger, uint256 fees);
    event FeeBurnt(address bridger, uint256 fees);
    event Retired(address sender, uint256 amount, uint256 eventId);

    // ----------------------------------------
    //              Modifiers
    // ----------------------------------------

    /// @dev modifier checks whether the `ToucanCarbonOffsetsFactory` is paused
    /// Since TCO2 contracts are permissionless, pausing does not function individually
    modifier whenNotPaused() {
        address tco2Factory = IToucanContractRegistry(contractRegistry)
            .toucanCarbonOffsetsFactoryAddress(standardRegistry());
        bool _paused = IPausable(tco2Factory).paused();
        require(!_paused, 'Paused TCO2');
        _;
    }

    modifier onlyBridges() {
        address tco2Factory = IToucanContractRegistry(contractRegistry)
            .toucanCarbonOffsetsFactoryAddress(standardRegistry());
        bool isAllowed = IToucanCarbonOffsetsFactory(tco2Factory)
            .allowedBridges(msg.sender);
        require(isAllowed, 'Not allowed');
        _;
    }

    modifier onlyFactoryOwner() {
        address tco2Factory = IToucanContractRegistry(contractRegistry)
            .toucanCarbonOffsetsFactoryAddress(standardRegistry());
        address owner = IToucanCarbonOffsetsFactory(tco2Factory).owner();
        require(owner == msg.sender, 'Not factory owner');
        _;
    }

    // Modifer that checks if the msg.sender has the required role
    modifier onlyWithRole(bytes32 role) {
        require(
            IToucanCarbonOffsetsFactory(
                IToucanContractRegistry(contractRegistry)
                    .toucanCarbonOffsetsFactoryAddress(standardRegistry())
            ).hasRole(role, msg.sender),
            'Invalid access'
        );
        _;
    }

    // Modifier that checks if msg.sender is the escrow contract
    modifier onlyEscrow() {
        require(
            IToucanContractRegistry(contractRegistry)
                .toucanCarbonOffsetsEscrowAddress() == msg.sender,
            'Not escrow contract'
        );
        _;
    }

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    // ----------------------------------------
    //      Bridge-related functions
    // ----------------------------------------

    /// @notice Burn TCO2 on behalf of a user. msg.sender does not require approval
    /// by the account for the burn to be successfull. This function is exposed so it
    /// can be utilized in cross-chain transfers of TCO2 where we want to burn the
    /// TCO2 in the source chain but not retire it.
    /// @param account The user for whom to burn TCO2
    /// @param amount The amount to burn.
    function bridgeBurn(address account, uint256 amount)
        external
        virtual
        whenNotPaused
        onlyBridges
    {
        _burn(account, amount);
    }

    /// @notice Mint TCO2 on behalf of a user. This function is exposed to
    /// be called by authorized message bridge systems and utilized for
    /// cross-chain transfers of TCO2 where we want to mint the TCO2 in the
    /// source chain.
    /// @param account The user for whom to mint TCO2
    /// @param amount The amount to mint.
    function bridgeMint(address account, uint256 amount)
        external
        virtual
        whenNotPaused
        onlyBridges
    {
        _mint(account, amount);
    }

    // ----------------------------------------
    //       Permissionless functions
    // ----------------------------------------

    function projectVintageTokenId() external view returns (uint256) {
        return _projectVintageTokenId;
    }

    /// @notice Token name getter overriden to return the a name based on the carbon project data
    //slither-disable-next-line external-function
    function name() public view virtual override returns (string memory) {
        string memory globalProjectId;
        string memory vintageName;
        (globalProjectId, vintageName) = getGlobalProjectVintageIdentifiers();
        return
            string.concat(
                'Toucan Protocol: TCO2-',
                globalProjectId,
                '-',
                vintageName
            );
    }

    /// @notice Token symbol getter overriden to return the a symbol based on the carbon project data
    //slither-disable-next-line external-function
    function symbol() public view virtual override returns (string memory) {
        string memory globalProjectId;
        string memory vintageName;
        (globalProjectId, vintageName) = getGlobalProjectVintageIdentifiers();
        return string.concat('TCO2-', globalProjectId, '-', vintageName);
    }

    /// @dev Helper function to retrieve data fragments for `name()` and `symbol()`
    function getGlobalProjectVintageIdentifiers()
        public
        view
        virtual
        returns (string memory, string memory)
    {
        ProjectData memory projectData;
        VintageData memory vintageData;
        (projectData, vintageData) = getAttributes();
        return (projectData.projectId, vintageData.name);
    }

    /// @dev Function to get corresponding attributes from the CarbonProjects
    function getAttributes()
        public
        view
        virtual
        returns (ProjectData memory, VintageData memory)
    {
        address pc = IToucanContractRegistry(contractRegistry)
            .carbonProjectsAddress();
        address vc = IToucanContractRegistry(contractRegistry)
            .carbonProjectVintagesAddress();

        VintageData memory vintageData = ICarbonProjectVintages(vc)
            .getProjectVintageDataByTokenId(_projectVintageTokenId);
        ProjectData memory projectData = ICarbonProjects(pc)
            .getProjectDataByTokenId(vintageData.projectTokenId);

        return (projectData, vintageData);
    }

    function getVintageData()
        external
        view
        virtual
        returns (VintageData memory vintageData)
    {
        address vc = IToucanContractRegistry(contractRegistry)
            .carbonProjectVintagesAddress();

        vintageData = ICarbonProjectVintages(vc).getProjectVintageDataByTokenId(
                _projectVintageTokenId
            );
    }

    /// @dev Returns the remaining space in TCO2 contract before hitting the cap
    function getRemaining() public view returns (uint256 remaining) {
        uint256 cap = getDepositCap();
        remaining = cap - totalSupply();
    }

    /// @dev Returns the cap for TCO2s based on `totalVintageQuantity`
    /// Returns `~unlimited` if the value for the vintage is not set
    function getDepositCap() public view returns (uint256) {
        VintageData memory vintageData;
        (, vintageData) = getAttributes();
        uint64 totalVintageQuantity = vintageData.totalVintageQuantity;

        ///@dev multipliying tonnes with decimals
        uint256 cap = totalVintageQuantity * 10**decimals();

        /// @dev if totalVintageQuantity is not set (=0), remove cap
        if (cap == 0) return type(uint256).max;

        return cap;
    }

    /// @notice Burn TCO2 on behalf of a user. msg.sender needs to be approved by
    /// the account for the burn to be successfull. This function is exposed so it
    /// can be utilized to burn credits without retiring them (eg. dispose HFC-23).
    /// @param account The user for whom to burn TCO2
    /// @param amount The amount to burn
    function burnFrom(address account, uint256 amount)
        external
        virtual
        whenNotPaused
    {
        _spendAllowance(account, msg.sender, amount);
        _burn(account, amount);
    }

    // @dev Internal function for the burning of TCO2 tokens
    // @dev retiringEntityAddress is a parameter to handle scenarios, when
    // retirements are performed from the escrow contract and the retiring entity
    // is different than the account.
    function _retire(
        address account,
        uint256 amount,
        address retiringEntityAddress
    ) internal virtual returns (uint256 retirementEventId) {
        _burn(account, amount);

        // Register retirement event in the certificates contract
        address certAddr = IToucanContractRegistry(contractRegistry)
            .retirementCertificatesAddress();
        retirementEventId = IRetirementCertificates(certAddr).registerEvent(
            retiringEntityAddress,
            _projectVintageTokenId,
            amount,
            false
        );

        emit Retired(retiringEntityAddress, amount, retirementEventId);
    }

    // @dev Internal function retire and mint certificates
    function _retireAndMintCertificate(
        address retiringEntity,
        CreateRetirementRequestParams memory params
    ) internal virtual whenNotPaused {
        uint256[] memory retirementEventIds;
        if (params.tokenIds.length == 0) {
            uint256 retirementEventId = _retire(
                msg.sender,
                params.amount,
                retiringEntity
            );
            retirementEventIds = new uint256[](1);
            retirementEventIds[0] = retirementEventId;
        } else {
            retirementEventIds = new uint256[](params.tokenIds.length);
            uint256 decimalMultiplier = 10**decimals();
            for (uint256 i = 0; i < params.tokenIds.length; i++) {
                //slither-disable-next-line unused-return
                (, uint256 batchQuantity, ) = ICarbonOffsetBatches(
                    IToucanContractRegistry(contractRegistry)
                        .carbonOffsetBatchesAddress()
                ).getBatchNFTData(params.tokenIds[i]);
                uint256 retirementEventId = _retire(
                    msg.sender,
                    batchQuantity * decimalMultiplier,
                    retiringEntity
                );
                retirementEventIds[i] = retirementEventId;
            }
        }
        //slither-disable-next-line unused-return
        IRetirementCertificates(
            IToucanContractRegistry(contractRegistry)
                .retirementCertificatesAddress()
        ).mintCertificateWithExtraData(
                retiringEntity,
                params,
                retirementEventIds
            );
    }

    // -----------------------------
    //      Locked ERC20 safety
    // -----------------------------

    /// @dev Modifier to disallowing sending tokens to either the 0-address
    /// or this contract itself
    modifier validDestination(address to) {
        require(to != address(0x0));
        require(to != address(this));
        _;
    }

    function transfer(address recipient, uint256 amount)
        public
        virtual
        override
        validDestination(recipient)
        whenNotPaused
        returns (bool)
    {
        super.transfer(recipient, amount);
        return true;
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    )
        public
        virtual
        override
        validDestination(recipient)
        whenNotPaused
        returns (bool)
    {
        super.transferFrom(sender, recipient, amount);
        return true;
    }

    /// @notice Return the name of the registry that this
    /// factory is enabling to tokenize, eg., verra
    /// @dev this must be overridden in the child contract
    function standardRegistry() public virtual returns (string memory) {}

    /// @notice Return the minimum precision supported by the registry
    /// @dev this must be overridden in the child contract
    function standardRegistryDecimals() public virtual returns (uint8) {}
}

contracts/bases/ToucanCarbonOffsetsDirec.content:
// SPDX-FileCopyrightText: 2023 Toucan Labs
//
// SPDX-License-Identifier: UNLICENSED

// If you encounter a vulnerability or an issue, please contact <security@toucan.earth> or visit security.toucan.earth
pragma solidity 0.8.14;

import './ToucanCarbonOffsetsBase.sol';

/// @notice Base contract for any TCO2 token that can be retired
/// directly on-chain.
abstract contract ToucanCarbonOffsetsDirectRetirements is
    ToucanCarbonOffsetsBase
{
    // ----------------------------------------
    //              Functions
    // ----------------------------------------

    /// @notice Retirement/Cancellation of TCO2 tokens (the actual offsetting),
    /// which results in the tokens being burnt
    function retire(uint256 amount)
        external
        virtual
        whenNotPaused
        returns (uint256 retirementEventId)
    {
        retirementEventId = _retire(msg.sender, amount, msg.sender);
    }

    /// @dev Allow for pools or third party contracts to retire for the user
    /// Requires approval
    function retireFrom(address account, uint256 amount)
        external
        virtual
        whenNotPaused
        returns (uint256 retirementEventId)
    {
        _spendAllowance(account, msg.sender, amount);
        retirementEventId = _retire(account, amount, account);
    }

    /// @notice Retire an amount of TCO2s, register a retirement event
    /// then mint a certificate passing a single retirementEventId.
    /// @param retiringEntityString An identifiable string for the retiring entity, eg. their name.
    /// @param beneficiary The beneficiary to set in the NFT.
    /// @param beneficiaryString The beneficiaryString to set in the NFT.
    /// @param retirementMessage The retirementMessage to set in the NFT.
    /// @param amount The amount to retire and issue an NFT certificate for.
    function retireAndMintCertificate(
        string calldata retiringEntityString,
        address beneficiary,
        string calldata beneficiaryString,
        string calldata retirementMessage,
        uint256 amount
    ) external virtual whenNotPaused {
        CreateRetirementRequestParams
            memory params = CreateRetirementRequestParams({
                tokenIds: new uint256[](0),
                amount: amount,
                retiringEntityString: retiringEntityString,
                beneficiary: beneficiary,
                beneficiaryString: beneficiaryString,
                retirementMessage: retirementMessage,
                beneficiaryLocation: '',
                consumptionCountryCode: '',
                consumptionPeriodStart: 0,
                consumptionPeriodEnd: 0
            });

        _retireAndMintCertificate(msg.sender, params);
    }
}

contracts/bases/ToucanCarbonOffsetsEscro.content:
// SPDX-FileCopyrightText: 2021 Toucan Labs
//
// SPDX-License-Identifier: UNLICENSED

// If you encounter a vulnerability or an issue, please contact <security@toucan.earth> or visit security.toucan.earth
pragma solidity 0.8.14;

import './ToucanCarbonOffsetsWithBatchBase.sol';
import {Errors} from '../libraries/Errors.sol';

/// @notice Base contract that can be reused between different TCO2
/// implementations that need to work with batch NFTs
abstract contract ToucanCarbonOffsetsEscrowableWithBatchBase is
    IERC721Receiver,
    ToucanCarbonOffsetsWithBatchBase
{
    // ----------------------------------------
    //       Events
    // ----------------------------------------

    event DetokenizationRequested(
        address indexed user,
        uint256 amount,
        uint256 indexed requestId,
        uint256[] batchIds
    );
    event DetokenizationReverted(uint256 indexed requestId);
    event DetokenizationFinalized(uint256 indexed requestId);

    event RetirementRequested(
        address indexed user,
        uint256 indexed requestId,
        CreateRetirementRequestParams params
    );
    event RetirementReverted(uint256 indexed requestId);
    event RetirementFinalized(uint256 indexed requestId);

    // ----------------------------------------
    //       Modifiers
    // ----------------------------------------

    modifier nonFractional(uint256 amount) {
        uint256 maxPrecision = 10**standardRegistryDecimals();
        require(
            amount == (amount / maxPrecision) * maxPrecision,
            Errors.TCO2_INVALID_DECIMALS
        );

        _;
    }

    // ----------------------------------------
    //       Admin functions
    // ----------------------------------------

    /// @notice Finalize a detokenization request by burning its amount of TCO2. In case the amount requested is
    /// smaller than the total amount of TCO2 in the batches, the last batch is split into two new batches, one that
    /// balances the total to be the amount requested and the other with the remaining amount.
    /// @dev Callable only by a detokenizer.
    /// @param requestId The id of the request to finalize.
    function finalizeDetokenization(uint256 requestId)
        external
        whenNotPaused
        onlyWithRole(DETOKENIZER_ROLE)
    {
        address escrow = IToucanContractRegistry(contractRegistry)
            .toucanCarbonOffsetsEscrowAddress();

        // Finalize escrow request
        IToucanCarbonOffsetsEscrow(escrow).finalizeDetokenizationRequest(
            requestId
        );

        emit DetokenizationFinalized(requestId);
    }

    /// @notice Revert a detokenization request
    /// @param requestId The request id in the escrow contract that
    /// tracks the detokenization request
    function revertDetokenization(uint256 requestId)
        external
        whenNotPaused
        onlyWithRole(DETOKENIZER_ROLE)
    {
        address escrow = IToucanContractRegistry(contractRegistry)
            .toucanCarbonOffsetsEscrowAddress();

        // Mark escrow request as reverted
        IToucanCarbonOffsetsEscrow(escrow).revertDetokenizationRequest(
            requestId
        );

        emit DetokenizationReverted(requestId);
    }

    /// @notice Finalize a retirement request by burning its amount of TCO2 and minting a certificate for the
    /// beneficiary. In case the amount requested is smaller than the total amount of TCO2 in the batches, the last
    /// batch is split into two new batches, one that balances the total to be the amount requested and the other with
    /// the remaining amount.
    /// @dev Callable only by a retirement approver.
    /// @param requestId The ID of the request to finalize.
    function finalizeRetirement(uint256 requestId)
        external
        whenNotPaused
        onlyWithRole(RETIREMENT_ROLE)
    {
        address escrow = IToucanContractRegistry(contractRegistry)
            .toucanCarbonOffsetsEscrowAddress();

        // Finalize escrow request
        IToucanCarbonOffsetsEscrow(escrow).finalizeRetirementRequest(requestId);

        emit RetirementFinalized(requestId);
    }

    /// @notice Revert a retirement request
    /// @param requestId The request id in the escrow contract that
    /// tracks the retirement request
    function revertRetirement(uint256 requestId)
        external
        whenNotPaused
        onlyWithRole(RETIREMENT_ROLE)
    {
        address escrow = IToucanContractRegistry(contractRegistry)
            .toucanCarbonOffsetsEscrowAddress();

        // Mark escrow request as reverted
        IToucanCarbonOffsetsEscrow(escrow).revertRetirementRequest(requestId);

        emit RetirementReverted(requestId);
    }

    // ----------------------------------------
    //       Permissionless functions
    // ----------------------------------------

    /// @notice Request a detokenization of batch-NFTs. The amount of TCO2 to detokenize will be transferred from
    /// the user to an escrow contract.
    /// @dev This function is permissionless and can be called by anyone
    /// @param tokenIds Token IDs of one or more batches to detokenize
    /// @param amount The amount of TCO2 to detokenize, must be greater than zero and equal to or smaller than the
    /// total amount of the batches (and also greater then the total amount of all the batches except the last one)
    /// @return requestId The ID of the request in the escrow contract
    function requestDetokenization(uint256[] calldata tokenIds, uint256 amount)
        external
        nonFractional(amount)
        whenNotPaused
        returns (uint256 requestId)
    {
        address escrow = IToucanContractRegistry(contractRegistry)
            .toucanCarbonOffsetsEscrowAddress();

        // Create escrow contract request, and transfer TCO2s from sender to escrow contract
        require(approve(escrow, amount), Errors.TCO2_APPROVAL_AMT_FAILED);
        requestId = IToucanCarbonOffsetsEscrow(escrow)
            .createDetokenizationRequest(_msgSender(), amount, tokenIds);

        emit DetokenizationRequested(_msgSender(), amount, requestId, tokenIds);
    }

    /// @notice Request a retirement of TCO2s from batch-NFTs. The amount of TCO2s to retire will be transferred
    /// from the user to an escrow contract.
    /// @dev This function is permissionless and can be called by anyone
    /// @param params The parameters of the retirement request:
    ///     uint256[] tokenIds One or more batches to retire
    ///     uint256 amount The amount of TCO2 to retire, must be greater than zero and equal to or smaller than the
    /// total amount of the batches (and also greater then the total amount of all the batches except the last one)
    ///     string retiringEntityString The name of the retiring entity
    ///     address beneficiary The address of the beneficiary of the retirement
    ///     string beneficiaryString The name of the beneficiary of the retirement
    ///     string retirementMessage A message to be included in the retirement certificate
    ///     string beneficiaryLocation The location of the beneficiary of the retirement
    ///     string consumptionCountryCode The country code of the consumption location
    ///     uint256 consumptionPeriodStart The start of the consumption period, in seconds since the epoch
    ///     uint256 consumptionPeriodEnd The end of the consumption period, in seconds since the epoch
    /// @return requestId The ID of the request in the escrow contract
    function requestRetirement(CreateRetirementRequestParams calldata params)
        external
        nonFractional(params.amount)
        whenNotPaused
        returns (uint256 requestId)
    {
        address escrow = IToucanContractRegistry(contractRegistry)
            .toucanCarbonOffsetsEscrowAddress();

        // Create escrow contract request, and transfer TCO2s from sender to escrow contract
        require(
            approve(escrow, params.amount),
            Errors.TCO2_APPROVAL_AMT_FAILED
        );
        requestId = IToucanCarbonOffsetsEscrow(escrow).createRetirementRequest(
            _msgSender(),
            params
        );

        emit RetirementRequested(_msgSender(), requestId, params);
    }

    function retireAndMintCertificateForEntity(
        address retiringEntity,
        CreateRetirementRequestParams calldata params
    ) external virtual onlyEscrow {
        _retireAndMintCertificate(retiringEntity, params);
    }
}

contracts/bases/ToucanCarbonOffsetsFacto.content:
// SPDX-FileCopyrightText: 2023 Toucan Labs
//
// SPDX-License-Identifier: UNLICENSED

// If you encounter a vulnerability or an issue, please contact <security@toucan.earth> or visit security.toucan.earth
pragma solidity 0.8.14;

import '@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol';
import '@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol';
import '@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol';
import '@openzeppelin/contracts/proxy/beacon/BeaconProxy.sol';

import '../bases/RoleInitializer.sol';
import '../interfaces/IToucanContractRegistry.sol';
import '../interfaces/ICarbonProjects.sol';
import '../interfaces/ICarbonProjectVintages.sol';
import '../libraries/ProjectUtils.sol';
import '../libraries/ProjectVintageUtils.sol';
import '../libraries/Strings.sol';
import '../libraries/Modifiers.sol';
import '../ToucanCarbonOffsetsFactoryStorage.sol';

/// @notice This TCO2 factory base should be used for any logic specific implementation
abstract contract ToucanCarbonOffsetsFactoryBase is
    ToucanCarbonOffsetsFactoryStorageV1,
    OwnableUpgradeable,
    PausableUpgradeable,
    UUPSUpgradeable,
    ProjectUtils,
    ProjectVintageUtils,
    Modifiers,
    ToucanCarbonOffsetsFactoryStorage,
    RoleInitializer
{
    using Strings for string;

    // ----------------------------------------
    //      Constants
    // ----------------------------------------

    /// @dev divider to calculate fees in basis points
    uint256 public constant bridgeFeeDivider = 1e4;

    /// @dev All roles related to accessing this contract
    bytes32 public constant DETOKENIZER_ROLE = keccak256('DETOKENIZER_ROLE');
    bytes32 public constant TOKENIZER_ROLE = keccak256('TOKENIZER_ROLE');

    // ----------------------------------------
    //      Events
    // ----------------------------------------

    event TokenCreated(uint256 vintageTokenId, address tokenAddress);
    event AddedToAllowedBridges(address account);
    event RemovedFromallowedBridges(address account);

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function __ToucanCarbonOffsetsFactoryBase_init(
        address[] calldata accounts,
        bytes32[] calldata roles
    ) internal {
        __Context_init_unchained();
        __Ownable_init_unchained();
        __Pausable_init_unchained();
        __UUPSUpgradeable_init_unchained();
        __RoleInitializer_init_unchained(accounts, roles);
    }

    // ----------------------------------------
    //           Admin functions
    // ----------------------------------------

    function _authorizeUpgrade(address newImplementation)
        internal
        virtual
        override
        onlyOwner
    {}

    /// @dev sets the Beacon that tracks the current implementation logic of the TCO2s
    function setBeacon(address _beacon) external onlyOwner {
        beacon = _beacon;
    }

    /// @notice Emergency function to disable contract's core functionality
    /// @dev wraps _pause(), only Admin
    function pause() external onlyBy(contractRegistry, owner()) {
        _pause();
    }

    /// @dev unpause the system, wraps _unpause(), only Admin
    function unpause() external onlyBy(contractRegistry, owner()) {
        _unpause();
    }

    /// @dev set the registry contract to be tracked
    function setToucanContractRegistry(address _address) external onlyOwner {
        contractRegistry = _address;
    }

    /// @notice adds account to the allowedBridges list
    /// meant to be used only for cross-chain bridging
    function addToAllowedBridges(address account) external onlyOwner {
        bool isAllowed = allowedBridges[account];
        require(!isAllowed, 'Already allowed');

        allowedBridges[account] = true;
        emit AddedToAllowedBridges(account);
    }

    /// @notice removes account from the allowedBridges list
    /// meant to be used only for cross-chain bridging
    function removeFromAllowedBridges(address account) external onlyOwner {
        bool isAllowed = allowedBridges[account];
        require(isAllowed, 'Already not allowed');

        allowedBridges[account] = false;
        emit RemovedFromallowedBridges(account);
    }

    // ----------------------------------------
    //       Permissionless functions
    // ----------------------------------------

    /// @notice internal factory function to deploy new TCO2 (ERC20) contracts
    /// @dev the function creates a new BeaconProxy for each TCO2
    /// @param projectVintageTokenId links the vintage-specific data to the TCO2 contract
    function deployNewProxy(uint256 projectVintageTokenId)
        internal
        virtual
        whenNotPaused
    {
        require(beacon != address(0), 'Error: Beacon for proxy not set');
        require(
            !checkExistence(projectVintageTokenId),
            'pvERC20 already exists'
        );
        checkProjectVintageTokenExists(contractRegistry, projectVintageTokenId);

        /// Ensure that the TCO2 to be deployed is for a standard that is supported
        /// by the standard registry.
        require(hasValidStandard(projectVintageTokenId), 'Invalid standard');

        /// @dev generate payload for initialize function
        string memory signature = 'initialize(string,string,uint256,address)';
        bytes memory payload = abi.encodeWithSignature(
            signature,
            'Toucan Protocol: TCO2',
            'TCO2',
            projectVintageTokenId,
            contractRegistry
        );

        //slither-disable-next-line reentrancy-no-eth
        BeaconProxy proxyTCO2 = new BeaconProxy(beacon, payload);

        IToucanContractRegistry(contractRegistry).addERC20(
            address(proxyTCO2),
            standardRegistry()
        );

        deployedContracts.push(address(proxyTCO2));
        pvIdtoERC20[projectVintageTokenId] = address(proxyTCO2);

        emit TokenCreated(projectVintageTokenId, address(proxyTCO2));
    }

    /// @dev Deploys a TCO2 contract based on a project vintage
    /// @param projectVintageTokenId numeric tokenId from vintage in `CarbonProjectVintages`
    function deployFromVintage(uint256 projectVintageTokenId)
        external
        virtual
        whenNotPaused
    {
        deployNewProxy(projectVintageTokenId);
    }

    /// @dev Checks if same project vintage has already been deployed
    function checkExistence(uint256 projectVintageTokenId)
        internal
        view
        virtual
        returns (bool)
    {
        if (pvIdtoERC20[projectVintageTokenId] == address(0)) {
            return false;
        } else {
            return true;
        }
    }

    function hasValidStandard(uint256 projectVintageTokenId)
        internal
        view
        returns (bool)
    {
        // Fetch contracts from contract registry
        address tcnRegistry = contractRegistry;
        address pc = IToucanContractRegistry(tcnRegistry)
            .carbonProjectsAddress();
        address vc = IToucanContractRegistry(tcnRegistry)
            .carbonProjectVintagesAddress();

        // Fetch carbon data
        VintageData memory vintageData = ICarbonProjectVintages(vc)
            .getProjectVintageDataByTokenId(projectVintageTokenId);
        ProjectData memory projectData = ICarbonProjects(pc)
            .getProjectDataByTokenId(vintageData.projectTokenId);

        // Check whether standard in carbon data matches supported standards
        // in the current factory
        string[] memory standards = supportedStandards();
        uint256 supportedStandardsLen = standards.length;
        string memory candidateStandard = projectData.standard;
        //slither-disable-next-line uninitialized-local
        for (uint256 i; i < supportedStandardsLen; ) {
            string memory supportedStandard = standards[i];

            if (candidateStandard.equals(supportedStandard)) {
                return true;
            }

            unchecked {
                ++i;
            }
        }

        return false;
    }

    /// @dev Returns all addresses of deployed TCO2 contracts
    function getContracts() external view virtual returns (address[] memory) {
        return deployedContracts;
    }

    function bridgeFeeReceiverAddress()
        external
        view
        virtual
        returns (address)
    {
        return bridgeFeeReceiver;
    }

    function getBridgeFeeAndBurnAmount(uint256 _quantity)
        external
        view
        virtual
        returns (uint256, uint256)
    {
        //slither-disable-next-line divide-before-multiply
        uint256 feeAmount = (_quantity * bridgeFeePercentageInBase) /
            bridgeFeeDivider;
        //slither-disable-next-line divide-before-multiply
        uint256 burnAmount = (feeAmount * bridgeFeeBurnPercentageInBase) /
            bridgeFeeDivider;
        return (feeAmount, burnAmount);
    }

    /// @notice Update the bridge fee percentage
    /// @param _bridgeFeePercentageInBase percentage of bridge fee in base
    function setBridgeFeePercentage(uint256 _bridgeFeePercentageInBase)
        external
        onlyOwner
    {
        require(
            _bridgeFeePercentageInBase < bridgeFeeDivider,
            'bridge fee percentage must be lower than bridge fee divider'
        );
        bridgeFeePercentageInBase = _bridgeFeePercentageInBase;
    }

    /// @notice Update the bridge fee receiver
    /// @param _bridgeFeeReceiver address to transfer the fees
    function setBridgeFeeReceiver(address _bridgeFeeReceiver)
        external
        onlyOwner
    {
        bridgeFeeReceiver = _bridgeFeeReceiver;
    }

    /// @notice Update the bridge fee burning percentage
    /// @param _bridgeFeeBurnPercentageInBase percentage of bridge fee in base
    function setBridgeFeeBurnPercentage(uint256 _bridgeFeeBurnPercentageInBase)
        external
        onlyOwner
    {
        require(
            _bridgeFeeBurnPercentageInBase < bridgeFeeDivider,
            'burn fee percentage must be lower than bridge fee divider'
        );
        bridgeFeeBurnPercentageInBase = _bridgeFeeBurnPercentageInBase;
    }

    /// @notice Update the bridge fee burn address
    /// @param _bridgeFeeBurnAddress address to transfer the fees to burn
    function setBridgeFeeBurnAddress(address _bridgeFeeBurnAddress)
        external
        onlyOwner
    {
        bridgeFeeBurnAddress = _bridgeFeeBurnAddress;
    }

    /// @notice Return the name of the registry that this
    /// factory is enabling to tokenize, eg., verra
    /// @dev this must be overridden in the child contract
    function standardRegistry() public pure virtual returns (string memory) {}

    /// @notice Return the standard(s) supported by the carbon
    /// registry from where this factory tokenizes credits, eg., VCS
    /// It's important to satisfy this interface in order to ensure
    /// that TCO2 factories cannot create TCO2s for standards that
    /// the standard registry does not support
    function supportedStandards()
        public
        pure
        virtual
        returns (string[] memory)
    {}
}

contracts/bases/ToucanCarbonOffsetsWithB.content:
// SPDX-FileCopyrightText: 2021 Toucan Labs
//
// SPDX-License-Identifier: UNLICENSED

// If you encounter a vulnerability or an issue, please contact <security@toucan.earth> or visit security.toucan.earth
pragma solidity 0.8.14;

import './ToucanCarbonOffsetsBase.sol';
import {Errors} from '../libraries/Errors.sol';

/// @notice Base contract that can be reused between different TCO2
/// implementations that need to work with batch NFTs
abstract contract ToucanCarbonOffsetsWithBatchBase is
    IERC721Receiver,
    ToucanCarbonOffsetsBase
{
    // ----------------------------------------
    //       Admin functions
    // ----------------------------------------

    /// @notice Defractionalize batch NFT by burning the amount
    /// of TCO2 from the sender and transfer the batch NFT that
    /// was selected to the sender.
    /// The only valid sender currently is the TCO2 factory owner.
    /// @param tokenId The batch NFT to defractionalize from the TCO2
    function defractionalize(uint256 tokenId)
        external
        whenNotPaused
        onlyFactoryOwner
    {
        address batchNFT = IToucanContractRegistry(contractRegistry)
            .carbonOffsetBatchesAddress();

        // Fetch and burn amount of the NFT to be defractionalized
        (
            ,
            uint256 batchAmount,
            BatchStatus status
        ) = _getNormalizedDataFromBatch(batchNFT, tokenId);
        require(
            status == BatchStatus.Confirmed,
            Errors.TCO2_BATCH_NOT_CONFIRMED
        );
        _burn(msg.sender, batchAmount);

        // Transfer batch NFT to sender
        IERC721(batchNFT).transferFrom(address(this), msg.sender, tokenId);
    }

    /// @notice Receive hook to fractionalize Batch-NFTs into ERC20's
    /// @dev Function is called with `operator` as `msg.sender` in a reference implementation by OZ
    /// `from` is the previous owner, not necessarily the same as operator.
    /// The hook checks if NFT collection is whitelisted and next if attributes are matching this ERC20 contract
    function onERC721Received(
        address, /* operator */
        address from,
        uint256 tokenId,
        bytes calldata /* data */
    ) external virtual override whenNotPaused returns (bytes4) {
        // msg.sender is the CarbonOffsetBatches contract
        require(
            checkWhiteListed(msg.sender),
            Errors.TCO2_BATCH_NOT_WHITELISTED
        );

        (
            uint256 gotVintageTokenId,
            uint256 quantity,
            BatchStatus status
        ) = _getNormalizedDataFromBatch(msg.sender, tokenId);
        require(
            gotVintageTokenId == _projectVintageTokenId,
            Errors.TCO2_NON_MATCHING_NFT
        );
        // don't mint TCO2s for received batches that are in detokenization/retirement requested status
        if (
            status == BatchStatus.DetokenizationRequested ||
            status == BatchStatus.RetirementRequested
        ) return this.onERC721Received.selector;
        // mint TCO2s for received batches that are in confirmed status
        require(
            status == BatchStatus.Confirmed,
            Errors.TCO2_BATCH_NOT_CONFIRMED
        );
        require(getRemaining() >= quantity, Errors.TCO2_QTY_HIGHER);

        minterToId[from] = tokenId;
        IToucanCarbonOffsetsFactory tco2Factory = IToucanCarbonOffsetsFactory(
            IToucanContractRegistry(contractRegistry)
                .toucanCarbonOffsetsFactoryAddress(standardRegistry())
        );
        address bridgeFeeReceiver = tco2Factory.bridgeFeeReceiverAddress();

        if (bridgeFeeReceiver == address(0x0)) {
            // if no bridge fee receiver address is set, mint without fees
            _mint(from, quantity);
        } else {
            // calculate bridge fees
            (uint256 feeAmount, uint256 feeBurnAmount) = tco2Factory
                .getBridgeFeeAndBurnAmount(quantity);
            _mint(from, quantity - feeAmount);
            address bridgeFeeBurnAddress = tco2Factory.bridgeFeeBurnAddress();
            // we mint the burn fee to the bridge fee burn address so it can be retired later.
            // if there is no address configured we just mint the full amount to the bridge fee receiver.
            if (bridgeFeeBurnAddress != address(0x0) && feeBurnAmount > 0) {
                feeAmount -= feeBurnAmount;
                _mint(bridgeFeeReceiver, feeAmount);
                _mint(bridgeFeeBurnAddress, feeBurnAmount);
                emit FeePaid(from, feeAmount);
                emit FeeBurnt(from, feeBurnAmount);
            } else if (feeAmount > 0) {
                _mint(bridgeFeeReceiver, feeAmount);
                emit FeePaid(from, feeAmount);
            }
        }

        return this.onERC721Received.selector;
    }

    // ----------------------------------------
    //       Internal functions
    // ----------------------------------------

    function _getNormalizedDataFromBatch(address cob, uint256 tokenId)
        internal
        view
        returns (
            uint256,
            uint256,
            BatchStatus
        )
    {
        (
            uint256 vintageTokenId,
            uint256 quantity,
            BatchStatus status
        ) = ICarbonOffsetBatches(cob).getBatchNFTData(tokenId);
        return (vintageTokenId, _batchAmountToTCO2Amount(quantity), status);
    }

    function _batchAmountToTCO2Amount(uint256 batchAmount)
        internal
        view
        returns (uint256)
    {
        return batchAmount * 10**decimals();
    }

    /// @dev Internal helper to check if CarbonOffsetBatches is whitelisted (official)
    function checkWhiteListed(address collection)
        internal
        view
        virtual
        returns (bool)
    {
        if (
            collection ==
            IToucanContractRegistry(contractRegistry)
                .carbonOffsetBatchesAddress()
        ) {
            return true;
        } else {
            return false;
        }
    }
}

contracts/CarbonOffsetBatches.sol.content:
// SPDX-FileCopyrightText: 2021 Toucan Labs
//
// SPDX-License-Identifier: UNLICENSED

// If you encounter a vulnerability or an issue, please contact <security@toucan.earth> or visit security.toucan.earth
pragma solidity 0.8.14;

import '@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol';
import '@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol';
import '@openzeppelin/contracts-upgradeable/token/ERC721/extensions/ERC721EnumerableUpgradeable.sol';
import '@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol';
import '@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol';
import '@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol';

import './interfaces/ICarbonOffsetBatches.sol';
import './interfaces/ICarbonProjectVintages.sol';
import './interfaces/IToucanCarbonOffsets.sol';
import './interfaces/IToucanCarbonOffsetsFactory.sol';
import './interfaces/IToucanContractRegistry.sol';
import './CarbonOffsetBatchesStorage.sol';
import {Errors} from './libraries/Errors.sol';
import './libraries/ProjectVintageUtils.sol';
import './libraries/Modifiers.sol';
import './libraries/Strings.sol';

/// @title A contract for managing batches of carbon credits
/// @notice Also referred to as Batch-Contract (formerly BatchCollection)
/// Contract that tokenizes retired/cancelled CO2 credits into NFTs via a claims process
contract CarbonOffsetBatches is
    ICarbonOffsetBatches,
    ERC721EnumerableUpgradeable,
    OwnableUpgradeable,
    PausableUpgradeable,
    AccessControlUpgradeable,
    UUPSUpgradeable,
    ProjectVintageUtils,
    Modifiers,
    CarbonOffsetBatchesStorage
{
    using SafeERC20Upgradeable for IERC20Upgradeable;
    using Strings for string;

    // ----------------------------------------
    //      Constants
    // ----------------------------------------

    /// @dev Version-related parameters. VERSION keeps track of production
    /// releases. VERSION_RELEASE_CANDIDATE keeps track of iterations
    /// of a VERSION in our staging environment.
    string public constant VERSION = '1.5.0';
    uint256 public constant VERSION_RELEASE_CANDIDATE = 1;

    /// @dev All roles related to accessing this contract
    bytes32 public constant VERIFIER_ROLE = keccak256('VERIFIER_ROLE');
    bytes32 public constant TOKENIZER_ROLE = keccak256('TOKENIZER_ROLE');

    // ----------------------------------------
    //      Events
    // ----------------------------------------

    event BatchMinted(address sender, uint256 tokenId);
    event BatchUpdated(uint256 tokenId, string serialNumber, uint256 quantity);
    event BatchLinkedWithVintage(
        uint256 tokenId,
        uint256 projectVintageTokenId
    );
    event BatchComment(
        uint256 tokenId,
        uint256 commentId,
        address sender,
        string comment
    );
    event BatchStatusUpdate(uint256 tokenId, BatchStatus status);
    event RegistrySupported(string registry, bool isSupported);
    event Tokenized(
        uint256 tokenId,
        address tco2,
        address indexed recipient,
        uint256 amount
    );

    event Split(uint256 tokenId, uint256 newTokenId);

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    // ----------------------------------------
    //      Upgradable related functions
    // ----------------------------------------

    function initialize(address _contractRegistry)
        external
        virtual
        initializer
    {
        __Context_init_unchained();
        __ERC721_init_unchained(
            'Toucan Protocol: Carbon Offset Batches',
            'TOUCAN-COB'
        );
        __Ownable_init_unchained();
        __Pausable_init_unchained();
        __AccessControl_init_unchained();
        __UUPSUpgradeable_init_unchained();

        contractRegistry = _contractRegistry;
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
    }

    function _authorizeUpgrade(address newImplementation)
        internal
        virtual
        override
        onlyOwner
    {}

    // ------------------------
    // Poor person's modifiers
    // ------------------------

    function onlyWithRole(bytes32 role) internal view {
        if (!hasRole(role, msg.sender)) revert(Errors.COB_INVALID_CALLER);
    }

    function onlyEscrow() internal view {
        address escrow = IToucanContractRegistry(contractRegistry)
            .toucanCarbonOffsetsEscrowAddress();
        if (escrow != msg.sender) revert(Errors.COB_INVALID_CALLER);
    }

    function onlyPending(uint256 tokenId) internal view {
        if (nftList[tokenId].status != BatchStatus.Pending)
            revert(Errors.COB_INVALID_STATUS);
    }

    function onlyApprovedOrOwner(uint256 tokenId) internal view {
        if (!_isApprovedOrOwner(_msgSender(), tokenId))
            revert(Errors.COB_TRANSFER_NOT_APPROVED);
    }

    function onlyVerifierOrBatchOwner(uint256 tokenId) internal view {
        if (
            ownerOf(tokenId) != _msgSender() &&
            !hasRole(VERIFIER_ROLE, msg.sender)
        ) revert(Errors.COB_NOT_VERIFIER_OR_BATCH_OWNER);
    }

    function onlyValidNewStatus(BatchStatus statusA, BatchStatus statusB)
        internal
        pure
    {
        if (statusA != statusB) revert(Errors.COB_INVALID_NEW_STATUS);
    }

    function onlyUnpaused() internal view {
        if (paused()) revert(Errors.COB_PAUSED_CONTRACT);
    }

    // ------------------------
    //      Admin functions
    // ------------------------

    /// @notice Emergency function to disable contract's core functionality
    /// @dev wraps _pause(), callable only by the Toucan contract registry or the contract owner
    function pause() external onlyBy(contractRegistry, owner()) {
        _pause();
    }

    /// @notice Emergency function to re-enable contract's core functionality after being paused
    /// @dev wraps _unpause(), callable only by the Toucan contract registry or the contract owner
    function unpause() external onlyBy(contractRegistry, owner()) {
        _unpause();
    }

    /// @notice Admin function to set the contract registry
    /// @dev Callable only by the contract owner
    /// @param _address The address of the new contract registry
    function setToucanContractRegistry(address _address) external onlyOwner {
        contractRegistry = _address;
    }

    /// @notice Admin function to set whether a registry is supported
    /// @dev Callable only by the contract owner; executable only if the status can be changed
    /// @param registry The registry to set supported status for
    /// @param isSupported Whether the registry should be supported
    function setSupportedRegistry(string memory registry, bool isSupported)
        external
        onlyOwner
    {
        if (supportedRegistries[registry] == isSupported)
            revert(Errors.COB_ALREADY_SUPPORTED);

        supportedRegistries[registry] = isSupported;
        emit RegistrySupported(registry, isSupported);
    }

    /// @dev internal helper function to set the status and emit an event
    function _updateStatus(uint256 tokenId, BatchStatus newStatus)
        internal
        virtual
    {
        nftList[tokenId].status = newStatus;
        emit BatchStatusUpdate(tokenId, newStatus);
    }

    /// @notice Set the status of a batch to a new status
    /// for detokenization or retirement requests.
    ///
    /// Valid transitions:
    /// - In case a user makes a request:
    ///   - Confirmed -> DetokenizationRequested
    ///   - Confirmed -> RetirementRequested
    /// - In case a DETOKENIZER_ROLE in TCO2 finalizes a request:
    ///   - DetokenizationRequested -> DetokenizationFinalized
    ///   - RetirementRequested -> RetirementFinalized
    /// - In case a DETOKENIZER_ROLE in TCO2 reverts a request:
    ///   - DetokenizationRequested -> Confirmed
    ///   - RetirementRequested -> Confirmed
    ///
    /// @dev Callable only by the escrow contract, only for batches owned by a TCO2 contract
    /// @param tokenId The token ID of the batch
    /// @param newStatus The new status to set
    function setStatusForDetokenizationOrRetirement(
        uint256 tokenId,
        BatchStatus newStatus
    ) external virtual override {
        onlyUnpaused();
        onlyEscrow();
        address tokenOwner = ownerOf(tokenId);
        if (!IToucanContractRegistry(contractRegistry).isValidERC20(tokenOwner))
            revert(Errors.COB_INVALID_BATCH_OWNER);
        BatchStatus currentStatus = nftList[tokenId].status;
        // Only valid transition to a requested status is from a confirmed batch
        if (
            newStatus == BatchStatus.DetokenizationRequested ||
            newStatus == BatchStatus.RetirementRequested
        ) {
            onlyValidNewStatus(currentStatus, BatchStatus.Confirmed);
            // Only valid transition to a finalized status is from a requested status
        } else if (newStatus == BatchStatus.DetokenizationFinalized) {
            onlyValidNewStatus(
                currentStatus,
                BatchStatus.DetokenizationRequested
            );
        } else if (newStatus == BatchStatus.RetirementFinalized) {
            onlyValidNewStatus(currentStatus, BatchStatus.RetirementRequested);
            // Only valid transition to a confirmed status is from a requested status
        } else if (newStatus == BatchStatus.Confirmed) {
            if (
                currentStatus != BatchStatus.DetokenizationRequested &&
                currentStatus != BatchStatus.RetirementRequested
            ) revert(Errors.COB_INVALID_NEW_STATUS);
        } else {
            revert(Errors.COB_INVALID_NEW_STATUS);
        }
        _updateStatus(tokenId, newStatus);
    }

    /// @notice Function to approve a Batch-NFT after validation.
    /// Fractionalization requires status Confirmed.
    /// @dev Callable only by verifiers, only for pending batches. This flow requires a previous linking with a vintage
    /// @param tokenId The token ID of the batch
    function confirmBatch(uint256 tokenId) external virtual {
        onlyUnpaused();
        onlyWithRole(VERIFIER_ROLE);
        _confirmBatch(tokenId);
    }

    /// @dev Internal function that requires a previous linking with a `projectVintageTokenId`.
    function _confirmBatch(uint256 _tokenId) internal {
        if (!_exists(_tokenId)) revert(Errors.COB_NOT_EXISTS);
        onlyPending(_tokenId);
        if (nftList[_tokenId].projectVintageTokenId == 0)
            revert(Errors.COB_MISSING_VINTAGE);
        if (serialNumberApproved[nftList[_tokenId].serialNumber])
            revert(Errors.COB_ALREADY_APPROVED);
        // setting serialnumber as unique after confirmation
        serialNumberApproved[nftList[_tokenId].serialNumber] = true;
        _updateStatus(_tokenId, BatchStatus.Confirmed);
    }

    /// @notice Reject Batch-NFTs, e.g. if the serial number entered is incorrect.
    /// @dev Callable only by verifiers, only for pending batches.
    /// @param tokenId The token ID of the batch
    function rejectBatch(uint256 tokenId) public virtual {
        onlyUnpaused();
        onlyWithRole(VERIFIER_ROLE);
        onlyPending(tokenId);

        // unsetting serialnumber with rejection
        serialNumberApproved[nftList[tokenId].serialNumber] = false;
        _updateStatus(tokenId, BatchStatus.Rejected);
    }

    /// @notice Function to reject Batch-NFTs, including a reason to be displayed to the user.
    function rejectWithComment(uint256 tokenId, string memory comment)
        external
        virtual
    {
        onlyUnpaused();
        rejectBatch(tokenId);
        _addComment(tokenId, comment);
    }

    /// @dev admin function to reject a previously approved batch
    /// Requires that the Batch-NFT has not been fractionalized yet
    function rejectApprovedWithComment(uint256 tokenId, string memory comment)
        external
    {
        onlyUnpaused();
        onlyWithRole(VERIFIER_ROLE);
        if (nftList[tokenId].status != BatchStatus.Confirmed)
            revert(Errors.COB_NOT_CONFIRMED);
        if (
            IToucanContractRegistry(contractRegistry).isValidERC20(
                ownerOf(tokenId)
            )
        ) revert(Errors.COB_ALREADY_FRACTIONALIZED);
        _updateStatus(tokenId, BatchStatus.Rejected);
        _addComment(tokenId, comment);
    }

    /// @notice Set batches back to pending after a rejection. This can
    /// be useful if there was an issue unrelated to the on-chain data of the
    /// batch, e.g. the batch was incorrectly rejected.
    /// @dev Callable only by verifiers, only for rejected batches.
    /// @param tokenId The token ID of the batch
    function setToPending(uint256 tokenId) external virtual {
        onlyUnpaused();
        onlyWithRole(VERIFIER_ROLE);
        if (nftList[tokenId].status != BatchStatus.Rejected)
            revert(Errors.COB_NOT_REJECTED);
        _updateStatus(tokenId, BatchStatus.Pending);
    }

    /// @notice Link Batch-NFT with Vintage
    /// @dev Function for alternative flow where Batch-NFT approval is done separately. Callable only by verifiers.
    /// @param tokenId The token ID of the batch
    /// @param projectVintageTokenId The token ID of the vintage
    function linkWithVintage(uint256 tokenId, uint256 projectVintageTokenId)
        external
        virtual
    {
        onlyUnpaused();
        onlyWithRole(VERIFIER_ROLE);
        _linkWithVintage(tokenId, projectVintageTokenId);
    }

    // @dev Function to internally link with Vintage when Batch-NFT approval is done seperately.
    function _linkWithVintage(uint256 _tokenId, uint256 _projectVintageTokenId)
        internal
    {
        checkProjectVintageTokenExists(
            contractRegistry,
            _projectVintageTokenId
        );
        nftList[_tokenId].projectVintageTokenId = _projectVintageTokenId;
        emit BatchLinkedWithVintage(_tokenId, _projectVintageTokenId);
    }

    /// @notice Link with vintage and confirm Batch-NFT
    /// @dev Function for main approval flow. Callable only by verifiers.
    /// @param tokenId The token ID of the batch
    /// @param projectVintageTokenId The token ID of the vintage
    function confirmBatchWithVintage(
        uint256 tokenId,
        uint256 projectVintageTokenId
    ) external virtual {
        onlyUnpaused();
        onlyWithRole(VERIFIER_ROLE);
        // We don't want this to be a "backdoor" for modifying the vintage; it
        // could be insecure or allow accidents to happen, and it would also
        // result in BatchLinkedWithVintage being emitted more than once per
        // batch.
        if (nftList[tokenId].projectVintageTokenId != 0)
            revert(Errors.COB_VINTAGE_ALREADY_SET);
        _linkWithVintage(tokenId, projectVintageTokenId);
        _confirmBatch(tokenId);
    }

    /// @notice Remove a previously approved serial number
    /// @dev Function to remove uniqueness for previously set serialnumbers. Callable only by verifiers.
    /// N.B. even though (technically speaking) calling this to complete the
    /// upgrade to a fixed contract is the responsibility of the contract's
    /// owner (deployer), in practice that is a multi-sig even before upgrade,
    /// and unsetting a bunch of serials via multi-sig is not practical.
    /// So instead we allow the verifiers to do it.
    /// @param serialNumber The serial number to unset
    function unsetSerialNumber(string memory serialNumber) external {
        onlyWithRole(VERIFIER_ROLE);
        serialNumberApproved[serialNumber] = false;
    }

    // ----------------------------------
    //  (Semi-)Permissionless functions
    // ----------------------------------

    /// @notice Permissionlessly mint empty Batch-NFTs
    /// Entry point to the carbon bridging process.
    /// @dev To be updated by NFT owner after serial number has been provided
    /// @param to The address the NFT should be minted to. This should be the user.
    /// @return The token ID of the newly minted NFT
    function mintEmptyBatch(address to) external virtual returns (uint256) {
        onlyUnpaused();
        return _mintEmptyBatch(to, to);
    }

    /// @notice Permissionlessly mint empty Batch-NFTs
    /// Entry point to the carbon bridging process.
    /// @dev To be updated by NFT owner after serial number has been provided
    /// @param to The address the NFT should be minted to. This should be the user
    /// but can also be the CarbonOffsetBatches contract itself in case the batch-NFT
    /// is held temporarily by the contract before fractionalization (see tokenize()).
    /// @param onBehalfOf The address of user on behalf of whom the batch is minted
    /// @return newItemId The token ID of the newly minted NFT
    function _mintEmptyBatch(address to, address onBehalfOf)
        internal
        returns (uint256 newItemId)
    {
        newItemId = batchTokenCounter;
        unchecked {
            ++newItemId;
        }
        batchTokenCounter = newItemId;

        _safeMint(to, newItemId);
        nftList[newItemId].status = BatchStatus.Pending;

        emit BatchMinted(onBehalfOf, newItemId);
    }

    /// @notice Update Batch-NFT after Serialnumber has been verified
    /// @dev Data is usually inserted by the user (NFT owner) via the UI. Callable only by verifiers or the batch-NFT
    /// owner.
    /// @param tokenId The token ID of the batch
    /// @param serialNumber The serial number received from the registry/credit cancellation
    /// @param quantity Quantity in tCO2e, greater than 0
    /// @param uri Optional tokenURI with additional information
    function updateBatchWithData(
        uint256 tokenId,
        string memory serialNumber,
        uint256 quantity,
        string memory uri
    ) external virtual {
        onlyUnpaused();
        onlyVerifierOrBatchOwner(tokenId);
        onlyPending(tokenId);
        _updateSerialAndQuantity(tokenId, serialNumber, quantity);

        if (!uri.equals(nftList[tokenId].uri)) nftList[tokenId].uri = uri;
    }

    /// @notice Internal function that updates batch-NFT after serial number has been verified
    function _updateSerialAndQuantity(
        uint256 tokenId,
        string memory serialNumber,
        uint256 quantity
    ) internal {
        if (serialNumberApproved[serialNumber])
            revert(Errors.COB_ALREADY_APPROVED);
        if (quantity == 0) revert(Errors.COB_INVALID_QUANTITY);
        nftList[tokenId].serialNumber = serialNumber;
        nftList[tokenId].quantity = quantity;

        emit BatchUpdated(tokenId, serialNumber, quantity);
    }

    /// @notice Update batch-NFT with serial number and quantity only
    /// @dev Convenience function to only update serial number and quantity and not the serial/URI. Callable only by a
    /// verifier or the batch-NFT owner.
    /// @param tokenId The token ID of the batch
    /// @param newSerialNumber The serial number received from the registry/credit cancellation
    /// @param newQuantity Quantity in tCO2e, greater than 0
    function setSerialandQuantity(
        uint256 tokenId,
        string memory newSerialNumber,
        uint256 newQuantity
    ) external virtual {
        onlyUnpaused();
        onlyVerifierOrBatchOwner(tokenId);
        onlyPending(tokenId);
        _updateSerialAndQuantity(tokenId, newSerialNumber, newQuantity);
    }

    /// @notice Returns just the confirmation (approval) status of Batch-NFT
    /// @param tokenId The token ID of the batch
    function getConfirmationStatus(uint256 tokenId)
        external
        view
        virtual
        override
        returns (BatchStatus)
    {
        return nftList[tokenId].status;
    }

    /// @notice Returns all data for Batch-NFT
    /// @dev Used in TCO2 contract's receive hook `onERC721Received`
    /// @param tokenId The token ID of the batch
    /// @return projectVintageTokenId The token ID of the vintage
    /// @return quantity Quantity in tCO2e
    /// @return status The status of the batch
    function getBatchNFTData(uint256 tokenId)
        external
        view
        virtual
        override
        returns (
            uint256,
            uint256,
            BatchStatus
        )
    {
        if (!_exists(tokenId)) revert(Errors.COB_NOT_EXISTS);
        return (
            nftList[tokenId].projectVintageTokenId,
            nftList[tokenId].quantity,
            nftList[tokenId].status
        );
    }

    /// @notice Returns the serial number of a batch token id
    /// @param tokenId The token ID of the batch
    /// @return The serial number of the batch
    function getSerialNumber(uint256 tokenId)
        external
        view
        virtual
        override
        returns (string memory)
    {
        if (!_exists(tokenId)) revert(Errors.COB_NOT_EXISTS);
        return nftList[tokenId].serialNumber;
    }

    /// @dev This is necessary because the automatically generated nftList
    /// getter will not include an array of comments in the returned tuple for
    /// gas reasons:
    /// https://docs.soliditylang.org/en/latest/contracts.html#visibility-and-getters
    function getComments(uint256 tokenId)
        external
        view
        virtual
        returns (string[] memory, address[] memory)
    {
        return (nftList[tokenId].comments, nftList[tokenId].commentAuthors);
    }

    /// @dev Overridden here because of function overloading issues with ethers.js
    function transferFrom(
        address from,
        address to,
        uint256 tokenId
    ) public virtual override(ERC721Upgradeable, IERC721Upgradeable) {
        onlyApprovedOrOwner(tokenId);
        safeTransferFrom(from, to, tokenId, '');
    }

    /// @notice Automatically converts Batch-NFT to TCO2s (ERC20)
    /// @dev Only by the batch-NFT owner or approved operator, only if batch is confirmed.
    /// Batch-NFT is sent from the sender and TCO2s are transferred to the sender.
    /// Queries the factory to find the corresponding TCO2 contract
    /// Fractionalization happens via receive hook on `safeTransferFrom`
    /// @param tokenId The token ID of the batch
    function fractionalize(uint256 tokenId) external virtual {
        onlyApprovedOrOwner(tokenId);
        // Fractionalize by transferring the batch-NFT to the TCO2 contract.
        safeTransferFrom(
            _msgSender(),
            _getTCO2ForBatchTokenId(tokenId),
            tokenId,
            ''
        );
    }

    /// @dev returns the address of the TCO2 contract that corresponds to the batch-NFT
    function _getTCO2ForBatchTokenId(uint256 tokenId)
        internal
        view
        returns (address)
    {
        uint256 pvId = nftList[tokenId].projectVintageTokenId;
        IToucanContractRegistry tcnRegistry = IToucanContractRegistry(
            contractRegistry
        );

        // Fetch the registry from the vintage data first
        address vintages = tcnRegistry.carbonProjectVintagesAddress();
        VintageData memory data = ICarbonProjectVintages(vintages)
            .getProjectVintageDataByTokenId(pvId);

        // Now we can fetch the TCO2 factory for the carbon registry
        string memory carbonRegistry = data.registry;
        if (bytes(carbonRegistry).length == 0) {
            carbonRegistry = 'verra';
        }
        address tco2Factory = tcnRegistry.toucanCarbonOffsetsFactoryAddress(
            carbonRegistry
        );

        return IToucanCarbonOffsetsFactory(tco2Factory).pvIdtoERC20(pvId);
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        virtual
        override(AccessControlUpgradeable, ERC721EnumerableUpgradeable)
        returns (bool)
    {
        return
            interfaceId == type(IAccessControlUpgradeable).interfaceId ||
            ERC721Upgradeable.supportsInterface(interfaceId);
    }

    function _baseURI() internal view virtual override returns (string memory) {
        return baseURI;
    }

    function setBaseURI(string memory gateway) external onlyOwner {
        baseURI = gateway;
    }

    /// @dev Returns the Uniform Resource Identifier (URI) for `tokenId` token.
    /// based on the ERC721URIStorage implementation
    function tokenURI(uint256 tokenId)
        public
        view
        virtual
        override
        returns (string memory)
    {
        if (!_exists(tokenId)) revert(Errors.COB_NOT_EXISTS);

        string memory uri = nftList[tokenId].uri;
        // If there is no base URI, return the token URI.
        if (bytes(_baseURI()).length == 0) return uri;
        // If both are set, concatenate the baseURI and tokenURI
        if (bytes(uri).length > 0) return string.concat(_baseURI(), uri);

        return super.tokenURI(tokenId);
    }

    /// @dev Utilized here in order to disable transfers when paused
    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount,
        uint256 batchSize
    ) internal virtual override {
        onlyUnpaused();
        super._beforeTokenTransfer(from, to, amount, batchSize);
    }

    /// @notice Append a comment to a Batch-NFT
    /// @dev Don't allow the contract owner to comment.  When the contract owner
    /// can also be a verifier they should add them as a verifier first; this
    /// should prevent accidental comments from the wrong account.
    function addComment(uint256 tokenId, string memory comment) external {
        // this also checks that tokenId exists, otherwise ERC721Upgradeable.ownerOf would revert on nonexistent token
        onlyVerifierOrBatchOwner(tokenId);
        _addComment(tokenId, comment);
    }

    function _addComment(uint256 tokenId, string memory comment) internal {
        nftList[tokenId].comments.push() = comment;
        nftList[tokenId].commentAuthors.push() = _msgSender();
        emit BatchComment(
            tokenId,
            nftList[tokenId].comments.length,
            _msgSender(),
            comment
        );
    }

    /// @notice This function allows external APIs to tokenize their carbon credits
    /// @dev Callable only by tokenizers. Performs the full tokenization process: minting a batch-NFT, linking it with a project
    /// vintage, setting the quantity and serial number, confirming the batch, and fractionalizing it. The TCO2s are
    /// then transferred to the recipient.
    /// @param recipient Recipient of the tokens
    /// @param serialNumber Serial number of the carbon credits to be tokenized
    /// @param quantity Quantity to be tokenized in 1e18 format, greater than 0
    /// @param projectVintageTokenId The token ID of the vintage
    /// @return tokenId The token ID of the newly minted batch
    function tokenize(
        address recipient,
        string calldata serialNumber,
        uint256 quantity,
        uint256 projectVintageTokenId
    ) external returns (uint256 tokenId) {
        onlyUnpaused();
        onlyWithRole(TOKENIZER_ROLE);
        // Prepare and confirm batch
        tokenId = _mintEmptyBatch(address(this), recipient);
        _updateSerialAndQuantity(tokenId, serialNumber, quantity);
        _linkWithVintage(tokenId, projectVintageTokenId);
        _confirmBatch(tokenId);

        // Check existing TCO2 balance; to be used at the end
        // to send the exact TCO2 needed to the recipient.
        address tco2 = _getTCO2ForBatchTokenId(tokenId);
        if (tco2 == address(0)) revert(Errors.COB_TCO2_NOT_FOUND);
        string memory registry = IToucanCarbonOffsets(tco2).standardRegistry();
        if (!supportedRegistries[registry])
            revert(Errors.COB_REGISTRY_NOT_SUPPORTED);
        uint256 balanceBefore = IERC20Upgradeable(tco2).balanceOf(
            address(this)
        );

        // Fractionalize by transferring the batch-NFT to the TCO2 contract.
        _safeTransfer(address(this), tco2, tokenId, '');

        // Check that TCO2s were minted
        uint256 balanceAfter = IERC20Upgradeable(tco2).balanceOf(address(this));
        uint256 amount = balanceAfter - balanceBefore;

        //slither-disable-next-line incorrect-equality
        if (amount == 0) revert(Errors.COB_NO_TCO2_MINTED);

        // Transfer minted TCO2s to recipient.
        IERC20Upgradeable(tco2).safeTransfer(recipient, amount);
        emit Tokenized(tokenId, tco2, recipient, amount);
    }

    /// @notice Split a batch-NFT into two batch-NFTs, by creating a new batch-NFT and updating the old one.
    /// The old batch will have a new serial number and quantity will be reduced by the quantity of the new batch.
    /// @dev Callable only by the escrow contract, only for batches with status
    /// RetirementRequested or DetokenizationRequested. The TCO2 contract will also be the owner of the new batch and
    /// its status will be the same as the old batch.
    /// @param tokenId The token ID of the batch to split
    /// @param tokenIdNewSerialNumber The new serial number for the old batch
    /// @param newTokenIdSerialNumber The serial number for the new batch
    /// @param newTokenIdQuantity The quantity for the new batch, must be smaller than the old quantity and greater
    /// than 0
    /// @return newTokenId The token ID of the new batch
    function split(
        uint256 tokenId,
        string calldata tokenIdNewSerialNumber,
        string calldata newTokenIdSerialNumber,
        uint256 newTokenIdQuantity
    ) external returns (uint256 newTokenId) {
        onlyUnpaused();
        onlyEscrow();
        address tco2 = ownerOf(tokenId);
        if (!IToucanContractRegistry(contractRegistry).isValidERC20(tco2))
            revert(Errors.COB_INVALID_BATCH_OWNER);
        // Validate batch status
        BatchStatus status = nftList[tokenId].status;
        if (
            status != BatchStatus.RetirementRequested &&
            status != BatchStatus.DetokenizationRequested
        ) revert(Errors.COB_INVALID_STATUS);
        // Validate batch quantity
        if (nftList[tokenId].quantity <= newTokenIdQuantity)
            revert(Errors.COB_INVALID_QUANTITY);

        // keep old serial number to be able to unapprove it after checking and approving the new ones
        string memory oldSerialNumber = nftList[tokenId].serialNumber;
        // this also performs the check that the new quantity is smaller than the old quantity, otherwise it would
        // underflow and revert. in case it is equal to the old quantity, _updateSerialAndQuantity would revert on
        // quantity == 0
        _updateSerialAndQuantity(
            tokenId,
            tokenIdNewSerialNumber,
            nftList[tokenId].quantity - newTokenIdQuantity
        );
        serialNumberApproved[tokenIdNewSerialNumber] = true;

        // mint a new batch to be owned by the TCO2 contract
        newTokenId = _mintEmptyBatch(address(this), tco2);
        _updateSerialAndQuantity(
            newTokenId,
            newTokenIdSerialNumber,
            newTokenIdQuantity
        ); // here we would revert in case newTokenIdQuantity == 0
        serialNumberApproved[newTokenIdSerialNumber] = true;

        // unapprove the old serial number
        serialNumberApproved[oldSerialNumber] = false;

        // link the new batch with the vintage of the old batch
        _linkWithVintage(newTokenId, nftList[tokenId].projectVintageTokenId);

        // Copy the status from the old batch
        _updateStatus(newTokenId, status);

        // transfer new batch to TCO2 contract with the right status so that
        // it will not be fractionalized
        _safeTransfer(address(this), tco2, newTokenId, '');

        emit Split(tokenId, newTokenId);
    }

    function onERC721Received(
        address, /* operator */
        address from, /* from */
        uint256, /* tokenId */
        bytes calldata /* data */
    ) external pure returns (bytes4) {
        // This hook is only used by the contract to mint batch-NFTs that
        // can be tokenized on behalf of end users.
        if (from != address(0)) revert(Errors.COB_ONLY_MINTS);
        return this.onERC721Received.selector;
    }
}

