// SPDX-FileCopyrightText: 2021 Toucan Labs
//
// SPDX-License-Identifier: UNLICENSED

// If you encounter a vulnerability or an issue, please contact <security@toucan.earth> or visit security.toucan.earth
pragma solidity 0.8.14;

import '../cross-chain/interfaces/IToucanCrosschainMessenger.sol';
import '../interfaces/IToucanCarbonOffsets.sol';
import {Errors} from '../libraries/Errors.sol';
import {Pool} from './Pool.sol';

abstract contract PoolBridgeable is Pool {
    // ----------------------------------------
    //      Events
    // ----------------------------------------

    event RouterUpdated(address router);

    event TCO2RebalanceRequestInitiated(
        uint32 indexed destinationDomain,
        address[] tco2s,
        uint256[] amounts
    );
    event TCO2RebalanceRequestCompleted(address[] tco2s, uint256[] amounts);

    // -------------------------------------
    //   Functions
    // -------------------------------------

    function onlyRouter() internal view {
        require(msg.sender == router, Errors.CP_ONLY_ROUTER);
    }

    /// @notice method to set router address
    /// @dev use this method to set router address
    /// @param _router address of ToucanCrosschainMessenger
    function setRouter(address _router) external {
        onlyPoolOwner();
        // router address can be set to zero to make bridgeMint and bridgeBurn unusable
        router = _router;
        emit RouterUpdated(_router);
    }

    /// @notice mint tokens to receiver account that were cross-chain bridged
    /// @dev invoked only by the ToucanCrosschainMessenger (Router)
    /// @param _account account that will be minted with corss-chain bridged tokens
    /// @param _amount amount of tokens that will be minted
    function bridgeMint(address _account, uint256 _amount) external {
        onlyRouter();
        _mint(_account, _amount);
    }

    /// @notice burn tokens from account to be cross-chain bridged
    /// @dev invoked only by the ToucanCrosschainMessenger (Router)
    /// @param _account account that will be burned with corss-chain bridged tokens
    /// @param _amount amount of tokens that will be burned
    function bridgeBurn(address _account, uint256 _amount) external {
        onlyRouter();
        _burn(_account, _amount);
    }

    function _getRemotePoolAddress(address tcm, uint32 destinationDomain)
        internal
        view
        returns (address recipient)
    {
        RemoteTokenInformation memory remoteInfo = IToucanCrosschainMessenger(
            tcm
        ).remoteTokens(address(this), destinationDomain);
        recipient = remoteInfo.tokenAddress;
        require(recipient != address(0), Errors.CP_EMPTY_ADDRESS);
    }

    /// @notice Get the fee needed to bridge TCO2s into the destination domain.
    /// @param destinationDomain The domain to bridge TCO2s to
    /// @param tco2s The TCO2s to bridge
    /// @param amounts The amounts of TCO2s to bridge
    /// @return fee The fee amount to be paid
    function quoteBridgeTCO2sFee(
        uint32 destinationDomain,
        address[] calldata tco2s,
        uint256[] calldata amounts
    ) external view returns (uint256 fee) {
        uint256 tco2Length = tco2s.length;
        _checkLength(tco2Length, amounts.length);

        address tcm = router;
        address recipient = _getRemotePoolAddress(tcm, destinationDomain);

        fee = IToucanCrosschainMessenger(tcm).quoteBridgeTCO2sFee(
            destinationDomain,
            tco2s,
            amounts,
            recipient
        );
    }

    /// @notice Allows MANAGER or the owner to bridge TCO2s into
    /// another domain.
    /// @param destinationDomain The domain to bridge TCO2s to
    /// @param tco2s The TCO2s to bridge
    /// @param amounts The amounts of TCO2s to bridge
    function bridgeTCO2s(
        uint32 destinationDomain,
        address[] calldata tco2s,
        uint256[] calldata amounts
    ) external payable {
        onlyWithRole(MANAGER_ROLE);
        uint256 tco2Length = tco2s.length;
        require(tco2Length != 0, Errors.CP_EMPTY_ARRAY);
        _checkLength(tco2Length, amounts.length);

        // TODO: Disallow bridging more TCO2s than an amount that
        // would bring the pool to imbalance, ie., end up with more
        // pool tokens than TCO2s in the pool in the source chain.

        // Read the address of the remote pool from ToucanCrosschainMessenger
        // and set that as a recipient in our cross-chain messages.
        address tcm = router;
        address recipient = _getRemotePoolAddress(tcm, destinationDomain);

        //slither-disable-next-line uninitialized-local
        for (uint256 i; i < tco2Length; ++i) {
            address tco2 = tco2s[i];
            uint256 amount = amounts[i];

            // Update supply-related storage variables in the pool
            VintageData memory vData = IToucanCarbonOffsets(tco2)
                .getVintageData();
            require(
                totalProjectSupply[vData.projectTokenId] >= amount,
                'insufficient vintage amount'
            );
            totalProjectSupply[vData.projectTokenId] -= amount;
            // this is enforced by an implicit invariant that makes
            // sure a single project supply can never be more than
            // the total underlying supply. Adding a check will
            // anyway make debugging easier should the invariant
            // unexpectedly be broken.
            require(
                totalUnderlyingSupply >= amount,
                'insufficient underlying supply'
            );
            totalUnderlyingSupply -= amount;
        }

        // Transfer tokens to recipient
        //slither-disable-next-line reentrancy-eth
        IToucanCrosschainMessenger(tcm).bridgeTCO2s{value: msg.value}(
            destinationDomain,
            tco2s,
            amounts,
            recipient
        );

        emit TCO2RebalanceRequestInitiated(destinationDomain, tco2s, amounts);
    }

    /// @notice Allows a router to complete the bridging of tokens.
    /// It transfers the tokens to the pool itself and updates the counters.
    /// @param tco2s The TCO2s that have been bridged
    /// @param amounts The amounts of TCO2s that have been bridged
    function completeTCO2Bridging(
        address[] calldata tco2s,
        uint256[] calldata amounts
    ) external {
        onlyRouter();
        uint256 tco2Length = tco2s.length;
        require(tco2Length != 0, Errors.CP_EMPTY_ARRAY);
        _checkLength(tco2Length, amounts.length);

        //slither-disable-next-line uninitialized-local
        for (uint256 i; i < tco2Length; ++i) {
            address tco2 = tco2s[i];
            uint256 amount = amounts[i];

            // Update supply-related storage variables in the pool
            VintageData memory vData = IToucanCarbonOffsets(tco2)
                .getVintageData();

            PoolVintageToken memory vintage = PoolVintageToken(
                tco2,
                0,
                vData.projectTokenId
            );

            // Update supply-related storage variables in the pool
            _changeSupply(vintage, int256(amount));

            // Transfer the underlying token to the pool
            _transfer(vintage, msg.sender, address(this), amount);
        }

        emit TCO2RebalanceRequestCompleted(tco2s, amounts);
    }
}

contracts/pools/PoolBridgeableLegacy.sol.content:
// SPDX-FileCopyrightText: 2021 Toucan Labs
//
// SPDX-License-Identifier: UNLICENSED

// If you encounter a vulnerability or an issue, please contact <security@toucan.earth> or visit security.toucan.earth
pragma solidity 0.8.14;

import '../cross-chain/interfaces/IToucanCrosschainMessenger.sol';
import '../interfaces/IToucanCarbonOffsets.sol';
import {Errors} from '../libraries/Errors.sol';
import {Pool} from './Pool.sol';

abstract contract PoolBridgeableLegacy is Pool {
    // ----------------------------------------
    //      Events
    // ----------------------------------------

    event RouterUpdated(address router);
    event TCO2Bridged(
        uint32 indexed destinationDomain,
        address indexed tco2,
        uint256 amount
    );

    // -------------------------------------
    //   Functions
    // -------------------------------------

    function onlyRouter() internal view {
        require(msg.sender == router, Errors.CP_ONLY_ROUTER);
    }

    /// @notice method to set router address
    /// @dev use this method to set router address
    /// @param _router address of ToucanCrosschainMessenger
    function setRouter(address _router) external {
        onlyPoolOwner();
        // router address can be set to zero to make bridgeMint and bridgeBurn unusable
        router = _router;
        emit RouterUpdated(_router);
    }

    /// @notice mint tokens to receiver account that were cross-chain bridged
    /// @dev invoked only by the ToucanCrosschainMessenger (Router)
    /// @param _account account that will be minted with corss-chain bridged tokens
    /// @param _amount amount of tokens that will be minted
    function bridgeMint(address _account, uint256 _amount) external {
        onlyRouter();
        _mint(_account, _amount);
    }

    /// @notice burn tokens from account to be cross-chain bridged
    /// @dev invoked only by the ToucanCrosschainMessenger (Router)
    /// @param _account account that will be burned with corss-chain bridged tokens
    /// @param _amount amount of tokens that will be burned
    function bridgeBurn(address _account, uint256 _amount) external {
        onlyRouter();
        _burn(_account, _amount);
    }

    function _getRemotePoolAddress(address tcm, uint32 destinationDomain)
        internal
        view
        returns (address recipient)
    {
        RemoteTokenInformation memory remoteInfo = IToucanCrosschainMessenger(
            tcm
        ).remoteTokens(address(this), destinationDomain);
        recipient = remoteInfo.tokenAddress;
        require(recipient != address(0), Errors.CP_EMPTY_ADDRESS);
    }

    /// @notice Get the fee needed to bridge TCO2s into the destination domain.
    /// @param destinationDomain The domain to bridge TCO2s to
    /// @param tco2s The TCO2s to bridge
    /// @param amounts The amounts of TCO2s to bridge
    /// @return fee The fee amount to be paid
    function quoteBridgeTCO2sFee(
        uint32 destinationDomain,
        address[] calldata tco2s,
        uint256[] calldata amounts
    ) external view returns (uint256 fee) {
        uint256 tco2Length = tco2s.length;
        _checkLength(tco2Length, amounts.length);

        address tcm = router;
        address recipient = _getRemotePoolAddress(tcm, destinationDomain);

        //slither-disable-next-line uninitialized-local
        for (uint256 i; i < tco2Length; ++i) {
            fee += IToucanCrosschainMessenger(tcm).quoteTokenTransferFee(
                destinationDomain,
                tco2s[i],
                amounts[i],
                recipient
            );
        }
    }

    /// @notice Allows MANAGER or the owner to bridge TCO2s into
    /// another domain.
    /// @param destinationDomain The domain to bridge TCO2s to
    /// @param tco2s The TCO2s to bridge
    /// @param amounts The amounts of TCO2s to bridge
    function bridgeTCO2s(
        uint32 destinationDomain,
        address[] calldata tco2s,
        uint256[] calldata amounts
    ) external payable {
        onlyWithRole(MANAGER_ROLE);
        uint256 tco2Length = tco2s.length;
        require(tco2Length != 0, Errors.CP_EMPTY_ARRAY);
        _checkLength(tco2Length, amounts.length);

        // TODO: Disallow bridging more TCO2s than an amount that
        // would bring the pool to imbalance, ie., end up with more
        // pool tokens than TCO2s in the pool in the source chain.

        // Read the address of the remote pool from ToucanCrosschainMessenger
        // and set that as a recipient in our cross-chain messages.
        address tcm = router;
        address recipient = _getRemotePoolAddress(tcm, destinationDomain);

        uint256 payment = msg.value / tco2Length;
        //slither-disable-next-line uninitialized-local
        for (uint256 i; i < tco2Length; ++i) {
            address tco2 = tco2s[i];
            uint256 amount = amounts[i];

            {
                // Update supply-related storage variables in the pool
                VintageData memory vData = IToucanCarbonOffsets(tco2)
                    .getVintageData();
                totalProjectSupply[vData.projectTokenId] -= amount;
                totalUnderlyingSupply -= amount;
            }

            // Transfer tokens to recipient
            //slither-disable-next-line reentrancy-eth
            IToucanCrosschainMessenger(tcm).transferTokensToRecipient{
                value: payment
            }(destinationDomain, tco2, amount, recipient);

            emit TCO2Bridged(destinationDomain, tco2, amount);
        }
    }
}

contracts/pools/PoolERC1155able.sol.content:
// SPDX-FileCopyrightText: 2024 Toucan Labs
//
// SPDX-License-Identifier: UNLICENSED

// If you encounter a vulnerability or an issue, please contact <security@toucan.earth> or visit security.toucan.earth
pragma solidity 0.8.14;

import '@openzeppelin/contracts/token/ERC1155/IERC1155.sol';
import '@openzeppelin/contracts/token/ERC1155/utils/ERC1155Holder.sol';
import '@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol';
import {FeeDistribution} from '@toucanprotocol/dynamic-fee-pools/src/interfaces/IFeeCalculator.sol';

import {Errors} from '../libraries/Errors.sol';
import {IPoolFilter} from './interfaces/IPoolFilter.sol';
import {Pool} from './Pool.sol';

abstract contract PoolERC1155able is Pool, ERC1155Holder {
    event ERC1155Deposited(
        address erc1155Addr,
        uint256 tokenId,
        uint256 amount
    );
    event ERC1155Redeemed(
        address account,
        address erc1155Addr,
        uint256 tokenId,
        uint256 amount
    );
    event UnderlyingDecimalsUpdated(uint8 decimals);

    /// @notice Set the underlying decimals for ERC-1155 tokens.
    /// @dev The underlying decimals are the number of decimals the ERC-1155
    /// token uses to represent the underlying asset. For example, if the
    /// ERC-1155 token represents a tonne of carbon, then the underlying
    /// decimals would be 0. If it represents a kilogram of carbon, then the
    /// underlying decimals would be 3.
    /// @param underlyingDecimals_ The number of decimals the ERC-1155 token
    /// uses to represent a tonne of carbon.
    function setUnderlyingDecimals(uint8 underlyingDecimals_) external {
        onlyPoolOwner();
        uint8 poolDecimals = decimals();
        // Underlying decimals cannot be higher than pool decimals, otherwise
        // the conversions in _poolTokenAmount and _underlyingAmount will fail.
        // Also if we allow a higher fidelity token to be deposited then the
        // difference in decimals will be unredeemable. In theory we could check
        // that low decimals in such deposits are not used but we don't have any
        // need to do that yet.
        if (underlyingDecimals_ > poolDecimals)
            revert(Errors.UNDERLYING_DECIMALS_TOO_HIGH);
        _underlyingDecimals = underlyingDecimals_;
        emit UnderlyingDecimalsUpdated(underlyingDecimals_);
    }

    /// @notice Function to limit the maximum pool supply
    /// @dev supplyCap is initially set to 0 and must be increased before deposits
    /// @param newCap New pool supply cap
    function setSupplyCap(uint256 newCap) external override {
        onlyPoolOwner();
        // The supply cap must be of valid precision for the underlying token
        uint256 precision = 10**(decimals() - _underlyingDecimals);
        if (newCap % precision != 0) {
            revert(Errors.INVALID_SUPPLY_CAP);
        }
        supplyCap = newCap;
        emit SupplyCapUpdated(newCap);
    }

    /// @notice Underlying decimals for ERC-1155 tokens. 0 decimals means
    /// that the smallest denomination the ERC-1155 token can represent is
    /// a tonne, 3 decimals means a kilogram, etc.
    function underlyingDecimals() external view returns (uint8) {
        return _underlyingDecimals;
    }

    /// @notice Return the total supply of the project for the
    /// given ERC1155 token.
    /// @return supply
    function totalPerProjectSupply(address erc1155, uint256 tokenId)
        external
        view
        returns (uint256)
    {
        return totalProjectSupply[_projectTokenId(erc1155, tokenId)];
    }

    /// @notice View function to calculate deposit fees pre-execution
    /// @dev User specifies in front-end the address and amount they want
    /// @param erc1155 ERC1155 contract address
    /// @param tokenId id representing the vintage
    /// @param amount Amount to redeem
    /// @return feeDistributionTotal Total fee amount to be paid
    function calculateDepositFees(
        address erc1155,
        uint256 tokenId,
        uint256 amount
    ) external view virtual returns (uint256 feeDistributionTotal);

    /// @notice View function to calculate redemption fees pre-execution,
    /// according to the amounts of pool tokens to be spent.
    /// @param erc1155s Array of ERC1155 contract addresses
    /// @param tokenIds ids of the vintages of each project
    /// @param amounts Array of pool token amounts to spend in order to redeem
    /// underlying tokens.
    /// @dev The indexes of all arrays should be matching 1:1.
    /// @param toRetire No-op, retirements of ERC-1155 tokens are not
    /// supported from within the pool yet and there are no immediate plans
    /// to add support.
    /// @return feeDistributionTotal Total fee amount to be paid
    function calculateRedemptionInFees(
        address[] memory erc1155s,
        uint256[] memory tokenIds,
        uint256[] memory amounts,
        bool toRetire
    ) external view virtual returns (uint256 feeDistributionTotal);

    /// @notice View function to calculate redemption fees pre-execution,
    /// according to the amounts of underlying tokens to be redeemed.
    /// @param erc1155s Array of ERC1155 contract addresses
    /// @param tokenIds ids of the vintages of each project
    /// @param amounts Array of underlying token amounts to redeem.
    /// @dev The indexes of all arrays should be matching 1:1.
    /// @param toRetire No-op, retirements of ERC-1155 tokens are not
    /// supported from within the pool yet and there are no immediate plans
    /// to add support.
    /// @return feeDistributionTotal Total fee amount to be paid
    function calculateRedemptionOutFees(
        address[] memory erc1155s,
        uint256[] memory tokenIds,
        uint256[] memory amounts,
        bool toRetire
    ) external view virtual returns (uint256 feeDistributionTotal);

    /// @notice Returns the balance of the carbon offset found in the pool
    /// @param erc1155 ERC1155 contract address
    /// @param tokenId id representing the vintage
    /// @return balance pool balance
    function tokenBalance(address erc1155, uint256 tokenId)
        public
        view
        returns (uint256 balance)
    {
        return IERC1155(erc1155).balanceOf(address(this), tokenId);
    }

    /// @notice Checks if token to be deposited is eligible for this pool.
    /// Reverts if not.
    /// Beware that the revert reason might depend on the underlying implementation
    /// of IPoolFilter.checkEligible
    /// @param erc1155 the ERC1155 contract to check
    /// @param tokenId the token id
    /// @return isEligible true if address is eligible and no other issues occur
    function checkEligible(address erc1155, uint256 tokenId)
        external
        view
        virtual
        returns (bool isEligible)
    {
        _checkEligible(_buildPoolVintageToken(erc1155, tokenId));
        return true;
    }

    // Overrides

    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(ERC1155Receiver, AccessControlUpgradeable)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }

    // Internal

    function _checkEligible(PoolVintageToken memory vintage)
        internal
        view
        override
    {
        string memory eligibilityError = IPoolFilter(filter)
            .checkERC1155Eligible(
                vintage.tokenAddress,
                vintage.erc1155VintageTokenId
            );
        if (bytes(eligibilityError).length > 0) {
            revert(eligibilityError);
        }
    }

    function _changeSupply(PoolVintageToken memory vintage, int256 delta)
        internal
        override
    {
        super._changeSupply(vintage, _underlyingAmount(delta));
    }

    function _emitDepositedEvent(
        PoolVintageToken memory vintage,
        uint256 amount
    ) internal override {
        emit ERC1155Deposited(
            vintage.tokenAddress,
            vintage.erc1155VintageTokenId,
            _underlyingAmount(amount)
        );
    }

    function _emitRedeemedEvent(PoolVintageToken memory vintage, uint256 amount)
        internal
        override
    {
        emit ERC1155Redeemed(
            msg.sender,
            vintage.tokenAddress,
            vintage.erc1155VintageTokenId,
            _underlyingAmount(amount)
        );
    }

    function _buildPoolVintageTokens(
        address[] memory erc1155s,
        uint256[] memory tokenIds
    ) internal view returns (PoolVintageToken[] memory vintageTokens) {
        uint256 length = erc1155s.length;
        vintageTokens = new PoolVintageToken[](length);
        for (uint256 i = 0; i < length; i++) {
            vintageTokens[i] = _buildPoolVintageToken(erc1155s[i], tokenIds[i]);
        }
    }

    function _buildPoolVintageToken(address erc1155, uint256 tokenId)
        internal
        view
        returns (PoolVintageToken memory)
    {
        return
            PoolVintageToken(
                erc1155,
                tokenId,
                _projectTokenId(erc1155, tokenId)
            );
    }

    function _feeDistribution(PoolVintageToken memory vintage, uint256 amount)
        internal
        view
        virtual
        override
        returns (FeeDistribution memory)
    {
        return
            feeCalculator.calculateDepositFees(
                address(this),
                vintage.tokenAddress,
                vintage.erc1155VintageTokenId,
                amount
            );
    }

    function _transfer(
        PoolVintageToken memory vintage,
        address from,
        address to,
        uint256 amount
    ) internal virtual override {
        IERC1155(vintage.tokenAddress).safeTransferFrom(
            from,
            to,
            vintage.erc1155VintageTokenId,
            _underlyingAmount(amount),
            ''
        );
    }

    function _retire(
        PoolVintageToken memory vintage,
        address from,
        uint256 amount
    ) internal override returns (uint256) {}

    function _projectTokenId(address erc1155, uint256 tokenId)
        internal
        view
        virtual
        returns (uint256);

    /// @dev The underlying amount conversions are helpful to execute
    /// so internal logic can be kept simple by always operating on
    /// pool token decimals. For example, underlying token amounts are
    /// converted to pool token amounts internally so pool ops like
    /// charging fees can be performed.
    function _underlyingAmount(uint256 poolTokenAmount)
        internal
        view
        returns (uint256)
    {
        uint8 decimals = decimals();
        decimals -= _underlyingDecimals;
        return poolTokenAmount / (10**decimals);
    }

    function _underlyingAmount(int256 poolTokenAmount)
        internal
        view
        returns (int256)
    {
        uint8 decimals = decimals();
        decimals -= _underlyingDecimals;
        // The minimum decimals value that can overflow in the int256
        // conversion below is 78 so not anything to be worried about.
        return poolTokenAmount / int256(10**decimals);
    }

    function _poolTokenAmount(uint256 underlyingAmount)
        internal
        view
        returns (uint256)
    {
        uint8 decimals = decimals();
        decimals -= _underlyingDecimals;
        return underlyingAmount * (10**decimals);
    }

    function _poolTokenAmounts(uint256[] memory amounts)
        internal
        view
        returns (uint256[] memory poolTokenAmounts)
    {
        uint256 length = amounts.length;
        poolTokenAmounts = new uint256[](length);
        for (uint256 i = 0; i < length; i++) {
            poolTokenAmounts[i] = _poolTokenAmount(amounts[i]);
        }
    }
}

contracts/pools/PoolERC20able.sol.content:
// SPDX-FileCopyrightText: 2024 Toucan Labs
//
// SPDX-License-Identifier: UNLICENSED

// If you encounter a vulnerability or an issue, please contact <security@toucan.earth> or visit security.toucan.earth
pragma solidity 0.8.14;

import '@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol';
import {FeeDistribution} from '@toucanprotocol/dynamic-fee-pools/src/interfaces/IFeeCalculator.sol';

import {VintageData, IToucanCarbonOffsets} from '../interfaces/IToucanCarbonOffsets.sol';
import {Errors} from '../libraries/Errors.sol';
import {IPoolFilter} from './interfaces/IPoolFilter.sol';
import {Pool} from './Pool.sol';

abstract contract PoolERC20able is Pool {
    using SafeERC20Upgradeable for IERC20Upgradeable;

    event Deposited(address erc20Addr, uint256 amount);
    event Redeemed(address account, address erc20, uint256 amount);

    /// @notice Return the total supply of the project for the
    /// given TCO2 token.
    /// @return supply
    function totalPerProjectSupply(address tco2)
        external
        view
        returns (uint256)
    {
        return totalProjectSupply[_projectTokenId(tco2)];
    }

    /// @notice Redeem a whitelisted TCO2 without paying any fees and burn
    /// the TCO2. Initially added to burn HFC-23 credits, can be used in the
    /// future to dispose of any other whitelisted credits.
    /// @dev User needs to approve the pool contract in the TCO2 contract for
    /// the amount to be burnt before executing this function.
    /// @param tco2 TCO2 to redeem and burn
    /// @param amount Amount to redeem and burn
    function redeemAndBurn(address tco2, uint256 amount) external {
        onlyUnpaused();
        require(redeemFeeExemptedTCO2s[tco2], Errors.CP_NOT_EXEMPTED);
        //slither-disable-next-line unused-return
        _redeemSingle(_buildPoolVintageToken(tco2), amount);
        // User has to approve the pool contract in the TCO2 contract
        // in order for this function to successfully burn the tokens
        IToucanCarbonOffsets(tco2).burnFrom(msg.sender, amount);
    }

    /// @notice View function to calculate deposit fees pre-execution
    /// @dev User specifies in front-end the address and amount they want
    /// @param tco2 TCO2 contract addresses
    /// @param amount Amount to deposit
    /// @return feeDistributionTotal Total fee amount to be paid
    function calculateDepositFees(address tco2, uint256 amount)
        external
        view
        virtual
        returns (uint256 feeDistributionTotal);

    /// @notice View function to calculate redemption fees pre-execution,
    /// according to the amounts of pool tokens to be spent.
    /// @param tco2s Array of TCO2 contract addresses
    /// @param amounts Array of pool token amounts to spend in order to redeem TCO2s.
    /// The indexes of this array are matching 1:1 with the tco2s array.
    /// @param toRetire Whether the TCO2s will be retired atomically
    /// with the redemption. It may be that lower fees will be charged
    /// in this case.
    /// @return feeDistributionTotal Total fee amount to be paid
    function calculateRedemptionInFees(
        address[] memory tco2s,
        uint256[] memory amounts,
        bool toRetire
    ) external view virtual returns (uint256 feeDistributionTotal);

    /// @notice View function to calculate redemption fees pre-execution,
    /// according to the amounts of TCO2 to be redeemed.
    /// @param tco2s Array of TCO2 contract addresses
    /// @param amounts Array of TCO2 amounts to redeem.
    /// The indexes of this array are matching 1:1 with the tco2s array.
    /// @param toRetire Whether the TCO2s will be retired atomically
    /// with the redemption. It may be that lower fees will be charged
    /// in this case.
    /// @return feeDistributionTotal Total fee amount to be paid
    function calculateRedemptionOutFees(
        address[] memory tco2s,
        uint256[] memory amounts,
        bool toRetire
    ) external view virtual returns (uint256 feeDistributionTotal);

    /// @notice Returns the balance of the carbon offset found in the pool
    /// @param tco2 TCO2 contract address
    /// @return balance pool balance
    function tokenBalance(address tco2) public view returns (uint256) {
        return IERC20Upgradeable(tco2).balanceOf(address(this));
    }

    /// @notice Checks if token to be deposited is eligible for this pool.
    /// Reverts if not.
    /// Beware that the revert reason might depend on the underlying implementation
    /// of IPoolFilter.checkEligible
    /// @param vintageToken the contract to check
    /// @return isEligible true if address is eligible and no other issues occur
    function checkEligible(address vintageToken)
        external
        view
        virtual
        returns (bool isEligible)
    {
        _checkEligible(_buildPoolVintageToken(vintageToken));
        return true;
    }

    // Internal

    function _emitDepositedEvent(
        PoolVintageToken memory vintage,
        uint256 amount
    ) internal override {
        emit Deposited(vintage.tokenAddress, amount);
    }

    function _emitRedeemedEvent(PoolVintageToken memory vintage, uint256 amount)
        internal
        override
    {
        emit Redeemed(msg.sender, vintage.tokenAddress, amount);
    }

    function _checkEligible(PoolVintageToken memory vintage)
        internal
        view
        override
    {
        //slither-disable-next-line unused-return
        string memory eligibilityError = IPoolFilter(filter).checkEligible(
            vintage.tokenAddress
        );

        if (bytes(eligibilityError).length > 0) {
            revert(eligibilityError);
        }
    }

    function _feeDistribution(PoolVintageToken memory vintage, uint256 amount)
        internal
        view
        virtual
        override
        returns (FeeDistribution memory)
    {
        return
            feeCalculator.calculateDepositFees(
                address(this),
                vintage.tokenAddress,
                amount
            );
    }

    function _transfer(
        PoolVintageToken memory vintage,
        address from,
        address to,
        uint256 amount
    ) internal virtual override {
        if (from == address(this)) {
            IERC20Upgradeable(vintage.tokenAddress).safeTransfer(to, amount);
        } else {
            IERC20Upgradeable(vintage.tokenAddress).safeTransferFrom(
                from,
                to,
                amount
            );
        }
    }

    function _retire(
        PoolVintageToken memory vintage,
        address from,
        uint256 amount
    ) internal override returns (uint256) {
        return
            IToucanCarbonOffsets(vintage.tokenAddress).retireFrom(from, amount);
    }

    function _buildPoolVintageTokens(address[] memory tco2s)
        internal
        view
        returns (PoolVintageToken[] memory vintages)
    {
        uint256 length = tco2s.length;
        vintages = new PoolVintageToken[](length);
        for (uint256 i = 0; i < length; i++) {
            vintages[i] = _buildPoolVintageToken(tco2s[i]);
        }
    }

    function _buildPoolVintageToken(address tco2)
        internal
        view
        returns (PoolVintageToken memory)
    {
        return PoolVintageToken(tco2, 0, _projectTokenId(tco2));
    }

    function _projectTokenId(address tco2) internal view returns (uint256) {
        return IToucanCarbonOffsets(tco2).getVintageData().projectTokenId;
    }
}

contracts/pools/PoolFilter.sol.content:
// SPDX-FileCopyrightText: 2023 Toucan Labs
//
// SPDX-License-Identifier: UNLICENSED

// If you encounter a vulnerability or an issue, please contact <security@toucan.earth> or visit security.toucan.earth
pragma solidity 0.8.14;

import '@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol';
import '@openzeppelin/contracts-upgradeable/utils/ContextUpgradeable.sol';
import '@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol';
import '@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol';

import '../bases/RoleInitializer.sol';
import '../interfaces/IToucanCarbonOffsets.sol';
import '../interfaces/IToucanContractRegistry.sol';
import {Errors} from '../libraries/Errors.sol';
import '../libraries/Strings.sol';
import './PoolFilterStorage.sol';

abstract contract PoolFilter is
    ContextUpgradeable,
    OwnableUpgradeable,
    PausableUpgradeable,
    RoleInitializer,
    UUPSUpgradeable,
    PoolFilterStorage
{
    using Strings for string;

    // ----------------------------------------
    //      Events
    // ----------------------------------------

    event AttributeMethodologyAdded(string methodology);
    event AttributeMethodologyRemoved(string methodology);
    event AttributeRegionAdded(string region);
    event AttributeRegionRemoved(string region);
    event AttributeStandardAdded(string standard);
    event AttributeStandardRemoved(string standard);
    event ExternalAddressRemovedFromAllowlist(address erc20addr);
    event ExternalAddressAllowlisted(address erc20addr);
    event ExternalERC1155TokenAllowlisted(
        address tokenAddress,
        uint256 tokenId
    );
    event ExternalERC1155TokenRemovedFromAllowlist(
        address tokenAddress,
        uint256 tokenId
    );
    event InternalAddressBlocklisted(address erc20addr);
    event InternalAddressRemovedFromBlocklist(address erc20addr);
    event InternalAddressRemovedFromAllowlist(address erc20addr);
    event InternalAddressAllowlisted(address erc20addr);
    event MappingSwitched(string mappingName, bool accepted);
    event MinimumVintageStartTimeUpdated(uint256 minimumVintageStartTime);
    event ToucanRegistrySet(address registry);

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function __PoolFilter_init_unchained(
        address[] calldata accounts,
        bytes32[] calldata roles
    ) internal initializer {
        __Context_init_unchained();
        __Ownable_init_unchained();
        __Pausable_init_unchained();
        __RoleInitializer_init_unchained(accounts, roles);
        __UUPSUpgradeable_init_unchained();
    }

    // ----------------------------------------
    //      Upgradable related functions
    // ----------------------------------------

    function _authorizeUpgrade(address) internal virtual override {
        onlyPoolOwner();
    }

    // ------------------------
    // Poor person's modifiers
    // ------------------------

    /// @dev function that checks whether the caller is the
    /// contract owner
    function onlyPoolOwner() internal view {
        require(owner() == msg.sender, Errors.CP_ONLY_OWNER);
    }

    // ----------------------------------------
    //      Read-only functions
    // ----------------------------------------

    /// @notice Checks if an ERC20 token is eligible for this pool
    /// @param erc20Addr The ERC20 token
    /// @return String with error code if any error occurs, else empty string
    function checkEligible(address erc20Addr)
        external
        view
        virtual
        returns (string memory)
    {
        bool isToucanContract = IToucanContractRegistry(contractRegistry)
            .isValidERC20(erc20Addr);

        if (isToucanContract) {
            if (internalAllowlist[erc20Addr]) {
                return '';
            }

            if (internalBlocklist[erc20Addr]) {
                return Errors.CP_BLOCKLISTED;
            }

            return checkAttributeMatching(erc20Addr);
        } else {
            /// @dev If not Toucan native contract, check if address is allowlisted
            if (!externalAllowlist[erc20Addr]) {
                return Errors.CP_NOT_ALLOWLISTED;
            }
        }

        return '';
    }

    /// @notice Checks if an ERC-1155 token is eligible for this pool
    /// @param tokenAddress address of the ERC1155 token
    /// @param tokenId ID of the ERC1155 token
    /// @return String with error code if any error occurs, else empty string
    function checkERC1155Eligible(address tokenAddress, uint256 tokenId)
        external
        view
        returns (string memory)
    {
        if (!externalERC1155Allowlist[tokenAddress][tokenId])
            return Errors.CP_NOT_ALLOWLISTED;

        return '';
    }

    /// @notice Checks whether incoming ERC20s match the accepted criteria/attributes
    /// @param erc20Addr The ERC20 token
    /// @return String with error code if any error occurs, else empty string
    function checkAttributeMatching(address erc20Addr)
        public
        view
        virtual
        returns (string memory)
    {
        ProjectData memory projectData;
        VintageData memory vintageData;
        (projectData, vintageData) = IToucanCarbonOffsets(erc20Addr)
            .getAttributes();

        /// @dev checks if any one of the attributes are blocklisted.
        /// If mappings are set to "allowlist"-mode, require the opposite
        if (vintageData.startTime < minimumVintageStartTime)
            return Errors.CP_START_TIME_TOO_OLD;

        if (regions[projectData.region] != regionsIsAcceptedMapping)
            return Errors.CP_REGION_NOT_ACCEPTED;

        if (standards[projectData.standard] != standardsIsAcceptedMapping)
            return Errors.CP_STANDARD_NOT_ACCEPTED;

        if (
            methodologies[projectData.methodology] !=
            methodologiesIsAcceptedMapping
        ) return Errors.CP_METHODOLOGY_NOT_ACCEPTED;

        return '';
    }

    // ----------------------------------------
    //      Admin functions
    // ----------------------------------------

    function setToucanContractRegistry(address _address) external {
        onlyPoolOwner();
        contractRegistry = _address;
        emit ToucanRegistrySet(_address);
    }

    /// @notice Generic function to switch attributes mappings into either
    /// acceptance or rejection criteria
    /// @param _mappingName attribute mapping of project-vintage data
    /// @param accepted determines if mapping works as a blocklist or allowlist
    function switchMapping(string memory _mappingName, bool accepted) external {
        onlyPoolOwner();
        if (_mappingName.equals('regions')) {
            accepted
                ? regionsIsAcceptedMapping = true
                : regionsIsAcceptedMapping = false;
        } else if (_mappingName.equals('standards')) {
            accepted
                ? standardsIsAcceptedMapping = true
                : standardsIsAcceptedMapping = false;
        } else if (_mappingName.equals('methodologies')) {
            accepted
                ? methodologiesIsAcceptedMapping = true
                : methodologiesIsAcceptedMapping = false;
        }
        emit MappingSwitched(_mappingName, accepted);
    }

    /// @notice Function to add attributes for filtering (does not support complex AttributeSets)
    /// @param addToList determines whether attribute should be added or removed
    /// Other params are arrays of attributes to be added
    function addAttributes(
        bool addToList,
        string[] memory _regions,
        string[] memory _standards,
        string[] memory _methodologies
    ) external virtual {
        onlyPoolOwner();
        //slither-disable-next-line uninitialized-local
        for (uint256 i; i < _standards.length; ++i) {
            if (addToList == true) {
                standards[_standards[i]] = true;
                emit AttributeStandardAdded(_standards[i]);
            } else {
                standards[_standards[i]] = false;
                emit AttributeStandardRemoved(_standards[i]);
            }
        }

        //slither-disable-next-line uninitialized-local
        for (uint256 i; i < _methodologies.length; ++i) {
            if (addToList == true) {
                methodologies[_methodologies[i]] = true;
                emit AttributeMethodologyAdded(_methodologies[i]);
            } else {
                methodologies[_methodologies[i]] = false;
                emit AttributeMethodologyRemoved(_methodologies[i]);
            }
        }

        //slither-disable-next-line uninitialized-local
        for (uint256 i; i < _regions.length; ++i) {
            if (addToList == true) {
                regions[_regions[i]] = true;
                emit AttributeRegionAdded(_regions[i]);
            } else {
                regions[_regions[i]] = false;
                emit AttributeRegionRemoved(_regions[i]);
            }
        }
    }

    /// @notice Function to allowlist selected external non-TCO2 contracts by their address
    /// @param erc20Addr accepts an array of contract addresses
    function addToExternalAllowlist(address[] memory erc20Addr) external {
        onlyPoolOwner();
        //slither-disable-next-line uninitialized-local
        for (uint256 i; i < erc20Addr.length; ++i) {
            externalAllowlist[erc20Addr[i]] = true;
            emit ExternalAddressAllowlisted(erc20Addr[i]);
        }
    }

    /// @notice Add ERC-1155 tokens to external allowlist
    /// @param tokenAddresses An array of contract addresses
    /// @param tokenIds An array of token IDs
    /// @dev Both arrays must be of the same length. Each token address is associated
    /// with the token ID at the same index.
    function addToExternalERC1155Allowlist(
        address[] calldata tokenAddresses,
        uint256[] calldata tokenIds
    ) external {
        onlyPoolOwner();
        uint256 tokensLen = tokenAddresses.length;
        if (tokensLen != tokenIds.length) revert(Errors.CP_LENGTH_MISMATCH);

        for (uint256 i = 0; i < tokensLen; ++i) {
            externalERC1155Allowlist[tokenAddresses[i]][tokenIds[i]] = true;
            emit ExternalERC1155TokenAllowlisted(
                tokenAddresses[i],
                tokenIds[i]
            );
        }
    }

    /// @notice Function to allowlist certain TCO2 contracts by their address
    /// @param erc20Addr accepts an array of contract addresses
    function addToInternalAllowlist(address[] memory erc20Addr) external {
        onlyPoolOwner();
        //slither-disable-next-line uninitialized-local
        for (uint256 i; i < erc20Addr.length; ++i) {
            internalAllowlist[erc20Addr[i]] = true;
            emit InternalAddressAllowlisted(erc20Addr[i]);
        }
    }

    /// @notice Function to blocklist certain TCO2 contracts by their address
    /// @param erc20Addr accepts an array of contract addresses
    function addToInternalBlocklist(address[] memory erc20Addr) external {
        onlyPoolOwner();
        //slither-disable-next-line uninitialized-local
        for (uint256 i; i < erc20Addr.length; ++i) {
            internalBlocklist[erc20Addr[i]] = true;
            emit InternalAddressBlocklisted(erc20Addr[i]);
        }
    }

    /// @notice Function to remove ERC20 addresses from external allowlist
    /// @param erc20Addr accepts an array of contract addresses
    function removeFromExternalAllowlist(address[] memory erc20Addr) external {
        onlyPoolOwner();
        //slither-disable-next-line uninitialized-local
        for (uint256 i; i < erc20Addr.length; ++i) {
            externalAllowlist[erc20Addr[i]] = false;
            emit ExternalAddressRemovedFromAllowlist(erc20Addr[i]);
        }
    }

    /// @notice Remove ERC-1155 tokens from external allowlist
    /// @param tokenAddresses An array of contract addresses
    /// @param tokenIds An array of token IDs
    /// @dev Both arrays must be of the same length. Each token address is associated
    /// with the token ID at the same index.
    function removeFromExternalERC1155Allowlist(
        address[] calldata tokenAddresses,
        uint256[] calldata tokenIds
    ) external {
        onlyPoolOwner();
        uint256 tokensLen = tokenAddresses.length;
        if (tokensLen != tokenIds.length) revert(Errors.CP_LENGTH_MISMATCH);

        for (uint256 i = 0; i < tokensLen; ++i) {
            externalERC1155Allowlist[tokenAddresses[i]][tokenIds[i]] = false;
            emit ExternalERC1155TokenRemovedFromAllowlist(
                tokenAddresses[i],
                tokenIds[i]
            );
        }
    }

    /// @notice Function to remove TCO2 addresses from internal blocklist
    /// @param erc20Addr accepts an array of contract addresses
    function removeFromInternalBlocklist(address[] memory erc20Addr) external {
        onlyPoolOwner();
        //slither-disable-next-line uninitialized-local
        for (uint256 i; i < erc20Addr.length; ++i) {
            internalBlocklist[erc20Addr[i]] = false;
            emit InternalAddressRemovedFromBlocklist(erc20Addr[i]);
        }
    }

    /// @notice Function to remove TCO2 addresses from internal allowlist
    /// @param erc20Addr accepts an array of contract addressesc
    function removeFromInternalAllowlist(address[] memory erc20Addr) external {
        onlyPoolOwner();
        //slither-disable-next-line uninitialized-local
        for (uint256 i; i < erc20Addr.length; ++i) {
            internalAllowlist[erc20Addr[i]] = false;
            emit InternalAddressRemovedFromAllowlist(erc20Addr[i]);
        }
    }

    /// @notice Determines the minimum vintage start time acceptance criteria of ERC20s
    /// @param _minimumVintageStartTime unix time format
    function setMinimumVintageStartTime(uint64 _minimumVintageStartTime)
        external
    {
        onlyPoolOwner();
        minimumVintageStartTime = _minimumVintageStartTime;
        emit MinimumVintageStartTimeUpdated(_minimumVintageStartTime);
    }
}

contracts/pools/PoolFilterStorage.sol.content:
// SPDX-FileCopyrightText: 2023 Toucan Labs
//
// SPDX-License-Identifier: UNLICENSED

// If you encounter a vulnerability or an issue, please contact <security@toucan.earth> or visit security.toucan.earth
pragma solidity 0.8.14;

abstract contract PoolFilterStorageV1 {
    /// @dev Mappings for attributes that can be included or excluded
    /// if set to `false`, attribute-values are blocklisted/rejected
    /// if set to `true`, attribute-values are allowlisted/accepted
    mapping(string => bool) public regions;
    mapping(string => bool) public standards;
    mapping(string => bool) public methodologies;

    /// @dev mapping to allowlist external non-TCO2 contracts by address
    mapping(address => bool) public externalAllowlist;

    /// @dev mapping to include certain TCO2 contracts by address,
    /// overriding attribute matching checks
    mapping(address => bool) public internalAllowlist;

    /// @dev mapping to exclude certain TCO2 contracts by address,
    /// even if the attribute matching would pass
    mapping(address => bool) public internalBlocklist;

    /// @dev address of the registry contract that knows
    /// the list of TCO2s
    address public contractRegistry;

    uint64 public minimumVintageStartTime;
    /// @dev These booleans control the direction of the criteria as
    /// they are defined in regions, standards, and methodologies.
    ///
    /// Two examples:
    ///
    /// 1. methodologiesIsAcceptedMapping=false
    ///    methodologies['VM0002']=true
    /// This means the pool will reject any project that follows the
    /// 'VM0002' methodology and accept anything else
    ///
    /// 2. methodologiesIsAcceptedMapping=true
    ///    methodologies['VM0002']=true
    /// This means the pool will accept only any project that follows the
    /// 'VM0002' methodology.
    bool public regionsIsAcceptedMapping;
    bool public standardsIsAcceptedMapping;
    bool public methodologiesIsAcceptedMapping;
}

abstract contract PoolFilterStorageV2 {
    /// @dev mapping to allowlist external ERC-1155 tokens
    mapping(address => mapping(uint256 => bool))
        public externalERC1155Allowlist;
}

abstract contract PoolFilterStorage is
    PoolFilterStorageV1,
    PoolFilterStorageV2
{}

contracts/pools/PoolStorage.sol.content:
// SPDX-FileCopyrightText: 2021 Toucan Labs
//
// SPDX-License-Identifier: UNLICENSED

// If you encounter a vulnerability or an issue, please contact <security@toucan.earth> or visit security.toucan.earth
pragma solidity 0.8.14;

import {IFeeCalculator} from '@toucanprotocol/dynamic-fee-pools/src/interfaces/IFeeCalculator.sol';

abstract contract PoolStorageV1 {
    /// @notice The supply cap is used as a measure to guard deposits
    /// in the pool. It is meant to minimize the impact a potential
    /// compromise in the source registry (eg. Verra) can have to the pool.
    uint256 public supplyCap;
    //slither-disable-next-line constable-states
    mapping(address => uint256) private DEPRECATED_tokenBalances;
    //slither-disable-next-line constable-states
    address private DEPRECATED_contractRegistry;

    /// @notice array used to read from when redeeming TCO2s automatically
    address[] public scoredTCO2s;

    /// @dev Mappings for attributes that can be included or excluded
    /// if set to `false`, attribute-values are blacklisted/rejected
    /// if set to `true`, attribute-values are whitelisted/accepted
    //slither-disable-next-line constable-states
    mapping(string => bool) private DEPRECATED_regions;
    //slither-disable-next-line constable-states
    mapping(string => bool) private DEPRECATED_standards;
    //slither-disable-next-line constable-states
    mapping(string => bool) private DEPRECATED_methodologies;

    /// @dev mapping to whitelist external non-TCO2 contracts by address
    //slither-disable-next-line constable-states
    mapping(address => bool) private DEPRECATED_externalWhiteList;

    /// @dev mapping to include certain TCO2 contracts by address,
    /// overriding attribute matching checks
    //slither-disable-next-line constable-states
    mapping(address => bool) private DEPRECATED_internalWhiteList;

    /// @dev mapping to exclude certain TCO2 contracts by address,
    /// even if the attribute matching would pass
    //slither-disable-next-line constable-states
    mapping(address => bool) private DEPRECATED_internalBlackList;

    /// @dev fees redeem receiver address
    //slither-disable-next-line uninitialized-state,constable-states
    address internal _feeRedeemReceiver;

    //slither-disable-next-line uninitialized-state,constable-states
    uint256 internal _feeRedeemPercentageInBase;

    /// @dev fees redeem burn address
    address internal _feeRedeemBurnAddress;

    /// @dev fees redeem burn percentage with 2 fixed decimals precision
    uint256 internal _feeRedeemBurnPercentageInBase;

    /// @dev repacked smaller variables here so new bools can be added below
    //slither-disable-next-line constable-states
    uint64 private DEPRECATED_minimumVintageStartTime;
    //slither-disable-next-line constable-states
    bool private DEPRECATED_seedMode;
    //slither-disable-next-line constable-states
    bool private DEPRECATED_regionsIsAcceptedMapping;
    //slither-disable-next-line constable-states
    bool private DEPRECATED_standardsIsAcceptedMapping;
    //slither-disable-next-line constable-states
    bool private DEPRECATED_methodologiesIsAcceptedMapping;
}

abstract contract PoolStorageV1_1 {
    /// @notice End users exempted from redeem fees
    mapping(address => bool) public redeemFeeExemptedAddresses;
}

abstract contract PoolStorageV1_2 {
    /// @notice TCO2s exempted from redeem fees
    mapping(address => bool) public redeemFeeExemptedTCO2s;
}

abstract contract PoolStorageV1_3 {
    /// @notice bridge router who has access to the bridgeMint & bridgeBurn functions which
    /// mint/burn pool tokens for cross chain messenges
    //slither-disable-next-line constable-states
    address public router;
}

abstract contract PoolStorageV1_4 {
    /// @notice fee percentage in basis points charged for selective
    /// redemptions that also retire the credits in the same transaction
    //slither-disable-next-line uninitialized-state,constable-states
    uint256 internal _feeRedeemRetirePercentageInBase;
    address public filter;
}

abstract contract PoolStorageV1_5 {
    /// @notice module to calculate fees for the pool
    //slither-disable-next-line uninitialized-state,constable-states
    IFeeCalculator public feeCalculator;
    /// @notice Total supply of underlying collateral in the pool.  Supply
    /// amounts are kept in the underlying's native precision regardless
    /// of whether underlying is an ERC-20 or ERC-1155 token.
    uint256 public totalUnderlyingSupply;
    /// @notice Project token id to total supply of the project
    /// in the pool. Supply amounts are kept in the underlying's native
    /// precision regardless of whether underlying is an ERC-20 or ERC-1155
    /// token.
    mapping(uint256 => uint256) public totalProjectSupply;
}

abstract contract PoolStorageV1_6 {
    /// @notice Mapping to track scores for ERC-1155 tokens
    mapping(address => mapping(uint256 => uint256)) internal _scores;

    mapping(address => mapping(uint256 => bool)) internal _vintageDeposited;
    /// @notice Underlying decimals for ERC-1155 tokens. 0 decimals means
    /// that the smallest denomination the ERC-1155 token can represent is
    /// a tonne, 3 decimals means a kilogram, etc.
    //slither-disable-next-line constable-states
    uint8 internal _underlyingDecimals;
}

abstract contract PoolStorageV1_7 {
    uint256 public minimumTCLSeedingThreshold;
}

abstract contract PoolStorage is
    PoolStorageV1,
    PoolStorageV1_1,
    PoolStorageV1_2,
    PoolStorageV1_3,
    PoolStorageV1_4,
    PoolStorageV1_5,
    PoolStorageV1_6,
    PoolStorageV1_7
{}

contracts/pools/PoolWithAdjustingERC1155.content:
// SPDX-FileCopyrightText: 2024 Toucan Labs
//
// SPDX-License-Identifier: UNLICENSED

// If you encounter a vulnerability or an issue, please contact <security@toucan.earth> or visit security.toucan.earth
pragma solidity 0.8.14;

import {Errors} from '../libraries/Errors.sol';
import {PoolWithFeeCalculatorERC1155} from './PoolWithFeeCalculatorERC1155.sol';

/// @notice Apply adjustments to the minting and burning of pool tokens.
/// Any pool for ERC-1155 tokens that inherits from this contract will be
/// able to control the amounts being minted and burned based on the scores
/// set for the ERC-1155 tokens.
abstract contract PoolWithAdjustingERC1155 is PoolWithFeeCalculatorERC1155 {
    // ----------------------------------------
    //      Events
    // ----------------------------------------

    event ScoreUpdated(address erc1155, uint256 tokenId, uint256 score);

    // ------------------------
    // Admin functions
    // ------------------------

    /// @notice Set scores for ERC-1155 vintage tokens. Scores range from 0 to
    /// 100 and determine the percentage of pool tokens minted or burnt during
    /// deposits or redemptions. A score of 0 disables depositing and redeeming
    /// for the vintage token.
    /// @dev Only executable by the pool owner
    /// @param erc1155s Array of ERC-1155 contracts
    /// @param tokenIds Array of ERC-1155 token IDs
    /// @param newScores Array of scores to set
    function setScores(
        address[] calldata erc1155s,
        uint256[] calldata tokenIds,
        uint256[] calldata newScores
    ) external {
        onlyPoolOwner();

        uint256 erc1155sLength = erc1155s.length;
        _checkLength(erc1155sLength, tokenIds.length);
        _checkLength(erc1155sLength, newScores.length);

        for (uint256 i = 0; i < erc1155sLength; ++i) {
            address erc1155 = erc1155s[i];
            uint256 tokenId = tokenIds[i];
            uint256 score = newScores[i];

            // Currently the smart contract does not provide logic to handle
            // score updates for already deposited tokens. This is problematic
            // because if the score of a deposited token is decreased, then it's
            // possible for redemptions to fail because there will not be enough
            // pool tokens held by the pool to burn. Until we implement this
            // missing logic, we disable score updates for already deposited
            // tokens altogether.
            require(
                _vintageDeposited[erc1155][tokenId] == false,
                'vintage already deposited'
            );

            require(erc1155 != address(0), Errors.CP_EMPTY_ADDRESS);
            require(score <= 100, Errors.INVALID_ERC1155_SCORE);

            _scores[erc1155][tokenId] = score;

            emit ScoreUpdated(erc1155, tokenId, score);
        }
    }

    // ----------------------------
    //   Permissionless functions
    // ----------------------------

    /// @notice Get the score for an ERC-1155 vintage token. Scores range from
    /// 0 to 100 and determine the percentage of pool tokens minted or burnt
    /// during deposits or redemptions. A score of 0 means that the vintage
    /// token cannot be deposited or redeemed.
    /// @param erc1155 Address of the ERC-1155 contract
    /// @param tokenId ID of the ERC-1155 token
    /// @return score The score of the ERC-1155 token
    function scores(address erc1155, uint256 tokenId)
        external
        view
        returns (uint256 score)
    {
        return _scores[erc1155][tokenId];
    }

    /// @notice Calculate the adjusted amount of pool tokens to mint
    /// for a deposit of the provided amount of the ERC-1155 token.
    /// @param erc1155 Address of the ERC-1155 contract
    /// @param tokenId ID of the ERC-1155 token
    /// @param amount Amount of ERC-1155 tokens to deposit
    /// @return adjustedAmount The amount of pool tokens minted to the user
    function calculateDepositAdjustedAmount(
        address erc1155,
        uint256 tokenId,
        uint256 amount
    ) external view returns (uint256 adjustedAmount) {
        onlyUnpaused();

        adjustedAmount = _calculateDepositAdjustedAmount(
            _buildPoolVintageToken(erc1155, tokenId),
            _poolTokenAmount(amount)
        );
    }

    /// @notice Calculate the amounts of pool tokens needed in order to
    /// redeem the provided amount of the ERC-1155 token.
    /// @param erc1155 Address of the ERC-1155 contract
    /// @param tokenId ID of the ERC-1155 token
    /// @param amount Amount of ERC-1155 token to redeem
    /// @return adjustedAmount The amount of pool tokens to be burnt
    /// by the caller
    function calculateRedeemOutAdjustedAmount(
        address erc1155,
        uint256 tokenId,
        uint256 amount
    ) external view returns (uint256 adjustedAmount) {
        onlyUnpaused();

        adjustedAmount = _calculateRedeemOutAdjustedAmount(
            _buildPoolVintageToken(erc1155, tokenId),
            _poolTokenAmount(amount)
        );
    }

    /// @notice Mint pool tokens based on the amount and score of the
    /// ERC-1155 token.
    /// @dev caller gets minted an adjusted amount, based on the
    /// score of the token, and the rest gets minted to the pool.
    /// @param account Address of the user to mint tokens to
    /// @param amount Amount of pool tokens to mint to the user. Adjustments
    /// will be made by this function based on the score of the vintage.
    /// @param feeDistributionTotal Fee in pool tokens paid by the user
    /// @param vintage ERC-1155 token vintage to be deposited
    /// @return callerMintedAmount The amount of pool tokens minted
    /// to the caller
    function _mint(
        address account,
        uint256 amount,
        uint256 feeDistributionTotal,
        PoolVintageToken memory vintage
    ) internal override returns (uint256 callerMintedAmount) {
        _vintageDeposited[vintage.tokenAddress][
            vintage.erc1155VintageTokenId
        ] = true;

        // The adjusted amount should be calculated based on the total minted pool tokens
        uint256 totalAmount = amount + feeDistributionTotal;
        uint256 adjustedAmount = _calculateDepositAdjustedAmount(
            vintage,
            totalAmount
        );
        callerMintedAmount = adjustedAmount - feeDistributionTotal;

        // mint adjusted amount to the user
        _mint(account, callerMintedAmount);

        // mint the remaining amount to the pool if an adjustment was made
        if (adjustedAmount < totalAmount)
            _mint(address(this), totalAmount - adjustedAmount);
    }

    /// @notice Burn pool tokens based on the amount and score of the
    /// ERC-1155 token.
    /// @dev caller burns an adjusted amount, based on the
    /// score of the token, and the rest gets burnt from the pool
    /// @param account Address of the user to burn tokens from
    /// @param amountE18 Amount of pool tokens to burn from the caller.
    /// Adjustments will be made by this function based on the score
    /// of the vintage.
    /// @param vintage ERC-1155 token vintage to be redeemed
    /// @return burntAmount The amount of pool tokens burnt by the caller
    function _burn(
        address account,
        uint256 amountE18,
        PoolVintageToken memory vintage
    ) internal override returns (uint256 burntAmount) {
        burntAmount = _calculateRedeemOutAdjustedAmount(vintage, amountE18);

        // burn the amount from user
        _burn(account, burntAmount);

        // burn the remaining amount using excess tokens from the pool
        if (amountE18 > burntAmount)
            _burn(address(this), amountE18 - burntAmount);
    }

    /// @notice Calculate the adjusted amount of pool tokens to mint
    /// for a deposit of the provided amount of the ERC-1155 token.
    /// @param vintage ERC-1155 token vintage to be deposited
    /// @param amountE18 Amount of ERC-1155 tokens to deposit, adjusted to
    /// 18 decimals
    /// @return The amount of pool tokens to be minted to the user
    function _calculateDepositAdjustedAmount(
        PoolVintageToken memory vintage,
        uint256 amountE18
    ) internal view returns (uint256) {
        uint256 score = _scores[vintage.tokenAddress][
            vintage.erc1155VintageTokenId
        ];
        require(score != 0, Errors.EMPTY_ERC155_SCORE);

        return (amountE18 * score) / 100;
    }

    /// @notice Calculate the amount of pool tokens to burn
    /// for a redemption of the provided amount of the ERC-1155 token.
    /// @param vintage ERC-1155 token vintage to be redeemed
    /// @param amountE18 Amount of ERC-1155 token to redeem, adjusted to 18 decimals
    /// @return The amount of pool tokens to be burnt
    function _calculateRedeemOutAdjustedAmount(
        PoolVintageToken memory vintage,
        uint256 amountE18
    ) internal view returns (uint256) {
        uint256 score = _scores[vintage.tokenAddress][
            vintage.erc1155VintageTokenId
        ];
        require(score != 0, Errors.EMPTY_ERC155_SCORE);

        return (amountE18 * score) / 100;
    }
}

contracts/pools/PoolWithFeeCalculatorERC.content:
// SPDX-FileCopyrightText: 2024 Toucan Labs
//
// SPDX-License-Identifier: UNLICENSED

// If you encounter a vulnerability or an issue, please contact <security@toucan.earth> or visit security.toucan.earth
pragma solidity 0.8.14;

import {FeeDistribution, IFeeCalculator} from '@toucanprotocol/dynamic-fee-pools/src/interfaces/IFeeCalculator.sol';

import {PoolERC1155able} from './PoolERC1155able.sol';
import {Errors} from '../libraries/Errors.sol';

/// @notice Pool with fee calculator template contract
/// Any pool that inherits from this contract will be able to
// charge fees both on deposits and redemptions with the use
/// of a fee calculator contract.
abstract contract PoolWithFeeCalculatorERC1155 is PoolERC1155able {
    // ----------------------------------------
    //      Events
    // ----------------------------------------

    event FeeCalculatorUpdated(address feeCalculator);

    // ------------------------
    // Admin functions
    // ------------------------

    /// @notice Update the address of the fee module contract
    /// @param _feeCalculator Fee module contract address
    function setFeeCalculator(address _feeCalculator) external {
        onlyPoolOwner();
        feeCalculator = IFeeCalculator(_feeCalculator);
        emit FeeCalculatorUpdated(_feeCalculator);
    }

    // ----------------------------
    //   Permissionless functions
    // ----------------------------

    /// @notice View function to calculate deposit fees pre-execution
    /// @dev User specifies in front-end the address and amount they want
    /// @param erc1155 ERC1155 contract address
    /// @param tokenId id representing the vintage
    /// @param amount Amount of ERC-1155 tokens to deposit (0 decimals)
    /// @return feeDistributionTotal Total fee amount to be paid in pool tokens
    function calculateDepositFees(
        address erc1155,
        uint256 tokenId,
        uint256 amount
    ) external view virtual override returns (uint256 feeDistributionTotal) {
        onlyUnpaused();

        // If the fee calculator is not configured or the amount doesn't bring us to the threshold, no fees are paid
        if (
            totalUnderlyingSupply + amount < minimumTCLSeedingThreshold ||
            address(feeCalculator) == address(0)
        ) {
            return 0;
        }

        uint256 chargeableAmount = totalUnderlyingSupply >=
            minimumTCLSeedingThreshold
            ? amount
            : amount + totalUnderlyingSupply - minimumTCLSeedingThreshold;
        FeeDistribution memory feeDistribution = feeCalculator
            .calculateDepositFees(
                address(this),
                erc1155,
                tokenId,
                _poolTokenAmount(chargeableAmount)
            );
        feeDistributionTotal = getFeeDistributionTotal(feeDistribution);
    }

    /// @notice View function to calculate redemption fees pre-execution,
    /// according to the amounts of pool tokens to be spent.
    /// NOTE: This function is not supported yet
    function calculateRedemptionInFees(
        address[] memory,
        uint256[] memory,
        uint256[] memory,
        bool
    ) external view virtual override returns (uint256) {
        revert(Errors.CP_NOT_SUPPORTED);
    }

    function _calculateRedemptionInFees(
        PoolVintageToken[] memory, /* ERC1155 vintages */
        uint256[] memory, /* amounts */
        bool /* toRetire */
    )
        internal
        pure
        override
        returns (
            uint256[] memory, /* feeAmounts */
            FeeDistribution memory /* feeDistribution */
        )
    {
        revert(Errors.CP_NOT_SUPPORTED);
    }

    /// @notice View function to calculate redemption fees pre-execution,
    /// according to the amounts of underlying tokens to be redeemed.
    /// @param erc1155s Array of ERC1155 contract addresses
    /// @param tokenIds ids of the vintages of each project
    /// @param amounts Array of ERC-1155 token amounts to redeem (0 decimals)
    /// The indexes of this array are matching 1:1 with the erc1155s array.
    /// @param toRetire No-op
    /// @return feeDistributionTotal Total fee amount to be paid in pool tokens
    function calculateRedemptionOutFees(
        address[] memory erc1155s,
        uint256[] memory tokenIds,
        uint256[] memory amounts,
        bool toRetire
    ) external view virtual override returns (uint256 feeDistributionTotal) {
        PoolVintageToken[] memory vintages = _buildPoolVintageTokens(
            erc1155s,
            tokenIds
        );

        (feeDistributionTotal, ) = _calculateRedemptionOutFees(
            vintages,
            _poolTokenAmounts(amounts),
            toRetire
        );
    }

    function _calculateRedemptionOutFees(
        PoolVintageToken[] memory vintages,
        uint256[] memory amountsE18,
        bool toRetire
    )
        internal
        view
        override
        returns (
            uint256 feeDistributionTotal,
            FeeDistribution memory feeDistribution
        )
    {
        onlyUnpaused();
        // Calculating fees for retiring is not supported yet
        require(!toRetire, Errors.CP_NOT_SUPPORTED);
        // Calculating fees for multi-ERC1155 redemptions is not supported yet
        uint256 vintageLength = vintages.length;
        require(vintageLength == 1, Errors.CP_NOT_SUPPORTED);
        _checkLength(vintageLength, amountsE18.length);

        // If the fee calculator is not configured or the caller is exempted, no fees are paid
        if (
            address(feeCalculator) == address(0) ||
            redeemFeeExemptedAddresses[msg.sender]
        ) {
            return (0, FeeDistribution(new address[](0), new uint256[](0)));
        }

        address[] memory erc1155s = new address[](vintageLength);
        uint256[] memory tokenIds = new uint256[](vintageLength);
        for (uint256 i = 0; i < vintageLength; i++) {
            erc1155s[i] = vintages[i].tokenAddress;
            tokenIds[i] = vintages[i].erc1155VintageTokenId;
        }

        feeDistribution = feeCalculator.calculateRedemptionFees(
            address(this),
            erc1155s,
            tokenIds,
            amountsE18
        );
        feeDistributionTotal = getFeeDistributionTotal(feeDistribution);
    }

    /// @notice Deposit function for pool that accepts ERC1155 vintages and mints pool token 1:1
    /// @param erc1155 ERC1155 contract address
    /// @param tokenId id representing the vintage
    /// @param amount Amount of ERC-1155 tokens to be deposited (0 decimals)
    /// @param maxFee Maximum pool token fee to be paid for the deposit. This value cannot be zero.
    /// Use `calculateDepositFees(erc1155,tokenId,amount)` to determine the fee that will be charged
    /// given the state of the pool during this call. Add a buffer on top of the returned
    /// fee amount up to the maximum fee you are willing to pay. (18 decimals)
    /// @dev Eligibility of the ERC1155 token to be deposited is checked via `checkEligible`
    /// @return mintedPoolTokenAmount Amount of pool tokens minted to the caller
    function deposit(
        address erc1155,
        uint256 tokenId,
        uint256 amount,
        uint256 maxFee
    ) external returns (uint256 mintedPoolTokenAmount) {
        if (address(feeCalculator) != address(0)) {
            require(maxFee != 0, Errors.CP_INVALID_MAX_FEE);
        }
        PoolVintageToken memory pvToken = _buildPoolVintageToken(
            erc1155,
            tokenId
        );
        return _deposit(pvToken, _poolTokenAmount(amount), maxFee);
    }

    /// @notice Redeem ERC1155 vintages for pool tokens 1:1 minus fees
    /// The amounts provided are the exact amounts of ERC1155 vintages to be redeemed.
    /// @param erc1155s ERC1155 contract address
    /// @param tokenIds id representing the vintage
    /// @param amounts Array of ERC-1155 token amounts to redeem (0 decimals)
    /// The indexes of this array are matching 1:1 with the erc1155s array.
    /// @param maxFee Maximum pool token fee to be paid for the redemption. This value cannot be zero.
    /// Use `calculateRedemptionOutFees(erc1155,tokenIds,amounts,false)` to determine the fee that will
    /// be charged given the state of the pool during this call. Add a buffer on top of the
    /// returned fee amount up to the maximum fee you are willing to pay. (18 decimals)
    /// @return poolAmountSpent The amount of pool tokens spent by the caller
    function redeemOutMany(
        address[] memory erc1155s,
        uint256[] memory tokenIds,
        uint256[] memory amounts,
        uint256 maxFee
    ) external virtual returns (uint256 poolAmountSpent) {
        if (address(feeCalculator) != address(0)) {
            require(maxFee != 0, Errors.CP_INVALID_MAX_FEE);
        }
        require(erc1155s.length == 1, Errors.CP_NOT_SUPPORTED);
        PoolVintageToken[] memory vintages = _buildPoolVintageTokens(
            erc1155s,
            tokenIds
        );

        (, poolAmountSpent) = _redeemOutMany(
            vintages,
            _poolTokenAmounts(amounts),
            maxFee,
            false
        );
    }
}

