pragma solidity 0.8.14;

import {DetokenizationRequest, RetirementRequest} from './ToucanCarbonOffsetsEscrowTypes.sol';

abstract contract ToucanCarbonOffsetsEscrowStorageV1 {
    address public contractRegistry;
    // Monotonically increasing request id counter
    uint256 public detokenizationRequestIdCounter;
    // Request id to request data
    mapping(uint256 => DetokenizationRequest) internal _detokenizationRequests;
    // Retirement request id counter
    uint256 public retirementRequestIdCounter;
    // Request id to request data
    mapping(uint256 => RetirementRequest) internal _retirementRequests;
}

abstract contract ToucanCarbonOffsetsEscrowStorage is
    ToucanCarbonOffsetsEscrowStorageV1
{}

contracts/ToucanCarbonOffsetsEscrowTypes.content:
// SPDX-FileCopyrightText: 2023 Toucan Labs
//
// SPDX-License-Identifier: UNLICENSED

// If you encounter a vulnerability or an issue, please contact <security@toucan.earth> or visit security.toucan.earth
pragma solidity 0.8.14;

struct DetokenizationRequest {
    address user;
    uint256 amount;
    RequestStatus status;
    // The request may optionally be associated with one or more batches.
    uint256[] batchTokenIds;
    uint256 projectVintageTokenId;
}

struct RetirementRequest {
    address user;
    uint256 amount;
    RequestStatus status;
    // The request may optionally be associated with one or more batches.
    // This may need to be limited to one batch for registries which don't
    // support atomic retirement of multiple batches in one go, since
    // retiring one batch at a time might create a situation where our
    // RetirementRequest is only partially fulfilled, and then we would be
    // stuck with no way forwards and no way to roll back.
    uint256[] batchTokenIds;
    // Optional
    string retiringEntityString;
    // Optional
    address beneficiary;
    // Optional
    string beneficiaryString;
    // Optional
    string retirementMessage;
    // Optional
    string beneficiaryLocation;
    // Optional
    string consumptionCountryCode;
    // Optional
    uint256 consumptionPeriodStart;
    // Optional
    uint256 consumptionPeriodEnd;
    uint256 projectVintageTokenId;
}

enum RequestStatus {
    Pending,
    Finalized,
    Reverted
}

contracts/ToucanCarbonOffsetsFactory.sol.content:
// SPDX-FileCopyrightText: 2021 Toucan Labs
//
// SPDX-License-Identifier: UNLICENSED

// If you encounter a vulnerability or an issue, please contact <security@toucan.earth> or visit security.toucan.earth
pragma solidity 0.8.14;

// ============ External Imports ============
import './bases/ToucanCarbonOffsetsFactoryBase.sol';

/// @notice This TCO2 factory creates project-vintage-specific ERC20 contracts for Batch-NFT fractionalization
/// Locks in received ERC721 Batch-NFTs and can mint corresponding quantity of ERC20s
/// Permissionless, anyone can deploy new ERC20s unless they do not yet exist and pid exists
contract ToucanCarbonOffsetsFactory is ToucanCarbonOffsetsFactoryBase {
    // ----------------------------------------
    //      Constants
    // ----------------------------------------

    /// @dev Version-related parameters. VERSION keeps track of production
    /// releases. VERSION_RELEASE_CANDIDATE keeps track of iterations
    /// of a VERSION in our staging environment.
    string public constant VERSION = '1.3.0';
    uint256 public constant VERSION_RELEASE_CANDIDATE = 1;

    // ----------------------------------------
    //      Upgradable related functions
    // ----------------------------------------

    function initialize(
        address _contractRegistry,
        address[] calldata _accounts,
        bytes32[] calldata _roles
    ) external virtual initializer {
        __ToucanCarbonOffsetsFactoryBase_init(_accounts, _roles);

        contractRegistry = _contractRegistry;
    }

    function setAdmin(address _user) external onlyOwner {
        grantRole(DEFAULT_ADMIN_ROLE, _user);
    }

    function standardRegistry() public pure override returns (string memory) {
        return 'verra';
    }

    function supportedStandards()
        public
        pure
        override
        returns (string[] memory)
    {
        string[] memory standards = new string[](1);
        standards[0] = 'VCS';
        return standards;
    }
}

