pragma solidity 0.8.14;

import {DetokenizationRequest, RetirementRequest} from './ToucanCarbonOffsetsEscrowTypes.sol';

abstract contract ToucanCarbonOffsetsEscrowStorageV1 {
    address public contractRegistry;
    // Monotonically increasing request id counter
    uint256 public detokenizationRequestIdCounter;
    // Request id to request data
    mapping(uint256 => DetokenizationRequest) internal _detokenizationRequests;
    // Retirement request id counter
    uint256 public retirementRequestIdCounter;
    // Request id to request data
    mapping(uint256 => RetirementRequest) internal _retirementRequests;
}

abstract contract ToucanCarbonOffsetsEscrowStorage is
    ToucanCarbonOffsetsEscrowStorageV1
{}
","keccak256":"0xfb4fcca4638cd21412231ae686b6f4d8188a5b3be08813f765031e8d9d12ab2d","license":"UNLICENSED"},"contracts/ToucanCarbonOffsetsEscrowTypes.sol":{"content":"// SPDX-FileCopyrightText: 2023 Toucan Labs
//
// SPDX-License-Identifier: UNLICENSED

// If you encounter a vulnerability or an issue, please contact <security@toucan.earth> or visit security.toucan.earth
pragma solidity 0.8.14;

struct DetokenizationRequest {
    address user;
    uint256 amount;
    RequestStatus status;
    // The request may optionally be associated with one or more batches.
    uint256[] batchTokenIds;
    uint256 projectVintageTokenId;
}

struct RetirementRequest {
    address user;
    uint256 amount;
    RequestStatus status;
    // The request may optionally be associated with one or more batches.
    // This may need to be limited to one batch for registries which don't
    // support atomic retirement of multiple batches in one go, since
    // retiring one batch at a time might create a situation where our
    // RetirementRequest is only partially fulfilled, and then we would be
    // stuck with no way forwards and no way to roll back.
    uint256[] batchTokenIds;
    // Optional
    string retiringEntityString;
    // Optional
    address beneficiary;
    // Optional
    string beneficiaryString;
    // Optional
    string retirementMessage;
    // Optional
    string beneficiaryLocation;
    // Optional
    string consumptionCountryCode;
    // Optional
    uint256 consumptionPeriodStart;
    // Optional
    uint256 consumptionPeriodEnd;
    uint256 projectVintageTokenId;
}

enum RequestStatus {
    Pending,
    Finalized,
    Reverted
}
","keccak256":"0x1f770f81cc66acad45c48c56036e8055d3b0c4579a16be46546c15e39c9e8c81","license":"UNLICENSED"},"contracts/bases/RoleInitializer.sol":{"content":"// SPDX-FileCopyrightText: 2023 Toucan Labs
