// If you encounter a vulnerability or an issue, please contact <security@toucan.earth> or visit security.toucan.earth
pragma solidity 0.8.14;

/**
 * @title Errors library
 * @notice Defines the error messages emitted by the different contracts of the Toucan protocol
 * @dev Inspired by the AAVE error library:
 * https://github.com/aave/protocol-v2/blob/5df59ec74a0c635d877dc1c5ee4a165d41488352/contracts/protocol/libraries/helpers/Errors.sol
 * Error messages prefix glossary:
 *  - CP = CarbonPool
 *  - COB = CarbonOffsetBatches
 *  - TCO2 = TCO2
 */
library Errors {
    // User is not authorized
    string public constant CP_UNAUTHORIZED = '1';
    // Empty array provided as input
    string public constant CP_EMPTY_ARRAY = '2';
    // Pool is full of TCO2s
    string public constant CP_FULL_POOL = '3';
    // ERC20 is blocklisted in the pool. This error
    // is returned for TCO2s that have been blocklisted
    // like the HFC-23 project.
    string public constant CP_BLOCKLISTED = '4';
    // ERC20 is not allowlisted in the pool
    // This error is returned in case the ERC20 is
    // not a TCO2 in which case it has to be manually
    // allowlisted in order to be allowed in the pool.
    string public constant CP_NOT_ALLOWLISTED = '5';
    // Vintage start time of a TCO2 is too old
    string public constant CP_START_TIME_TOO_OLD = '6';
    string public constant CP_REGION_NOT_ACCEPTED = '7';
    string public constant CP_STANDARD_NOT_ACCEPTED = '8';
    string public constant CP_METHODOLOGY_NOT_ACCEPTED = '9';
    // Provided fee is invalid, not in a basis points format: [0,10000)
    string public constant CP_INVALID_FEE = '10';
    // Provided address needs to be non-zero
    string public constant CP_EMPTY_ADDRESS = '11';
    // Validation check to ensure array lengths match
    string public constant CP_LENGTH_MISMATCH = '12';
    // TCO2 not exempted from redeem fees
    string public constant CP_NOT_EXEMPTED = '13';
    // A contract has been paused
    string public constant CP_PAUSED_CONTRACT = '14';
    // Redemption has leftover unredeemed value
    string public constant CP_NON_ZERO_REMAINING = '15';
    // Redemption exceeds deposited TCO2 supply
    string public constant CP_EXCEEDS_TCO2_SUPPLY = '16';
    // User must be a router
    string public constant CP_ONLY_ROUTER = '17';
    // User must be the owner
    string public constant CP_ONLY_OWNER = '18';
    // Zero destination address is invalid for pool token transfers
    string public constant CP_INVALID_DESTINATION_ZERO = '19';
    // Self destination address is invalid for pool token transfers
    string public constant CP_INVALID_DESTINATION_SELF = '20';
    // Zero amount provided as an input (eg., in redemptions) in invalid
    string public constant CP_ZERO_AMOUNT = '21';
    // ERC20 is not eligible to be pooled
    string public constant CP_NOT_ELIGIBLE = '22';
    // Carbon registry is already supported in COB
    string public constant COB_ALREADY_SUPPORTED = '23';
    // The caller is not granted the VERIFIER_ROLE in COB
    string public constant COB_NOT_VERIFIER_OR_BATCH_OWNER = '24';
    // The caller does not own the provided batch
    string public constant COB_NOT_BATCH_OWNER = '25';
    // The owner of the batch is invalid (not a TCO2 contract)
    string public constant COB_INVALID_BATCH_OWNER = '26';
    // The batch is not in Confirmed status
    string public constant COB_NOT_CONFIRMED = '27';
    // The batch is not in a requested status (DetokenizationRequested or RetirementRequested)
    string public constant COB_NOT_REQUESTED_STATUS = '28';
    // The batch does not exist
    string public constant COB_NOT_EXISTS = '29';
    // The batch has an invalid status based on the action requested
    string public constant COB_INVALID_STATUS = '30';
    // The batch is missing an associated project vintage
    string public constant COB_MISSING_VINTAGE = '31';
    // The serial number in the batch is already approved
    string public constant COB_ALREADY_APPROVED = '32';
    // The batch is not in Pending status
    string public constant COB_NOT_PENDING = '33';
    // The batch is already fractionalized
    string public constant COB_ALREADY_FRACTIONALIZED = '34';
    // The batch is not in Rejected status
    string public constant COB_NOT_REJECTED = '35';
    // The project vintage is already set in the batch
    string public constant COB_VINTAGE_ALREADY_SET = '36';
    // The transfer is not approved
    string public constant COB_TRANSFER_NOT_APPROVED = '37';
    // The COB contract is paused
    string public constant COB_PAUSED_CONTRACT = '38';
    // The caller is invalid
    string public constant COB_INVALID_CALLER = '39';
    // The TCO2 for the batch is not found
    string public constant COB_TCO2_NOT_FOUND = '40';
    // The registry for the provided vintage is not supported
    string public constant COB_REGISTRY_NOT_SUPPORTED = '41';
    // No TCO2 was minted as part of tokenization
    string public constant COB_NO_TCO2_MINTED = '42';
    // Only mints are supported for the batch contract to receive an NFT
    string public constant COB_ONLY_MINTS = '43';
    // New batch status is invalid
    string public constant COB_INVALID_NEW_STATUS = '44';
    // The TCO2 batch amount is invalid
    string public constant TCO2_BATCH_AMT_INVALID = '45';
    // The TCO2 batch amount approval has failed
    string public constant TCO2_APPROVAL_AMT_FAILED = '46';
    // The TCO2 batch not confirmed
    string public constant TCO2_BATCH_NOT_CONFIRMED = '47';
    // The TCO2 batch not whitelisted
    string public constant TCO2_BATCH_NOT_WHITELISTED = '48';
    // The TCO2 is non matching NFT
    string public constant TCO2_NON_MATCHING_NFT = '49';
    // The TCO2 Quantity in batch is higher than total vintages
    string public constant TCO2_QTY_HIGHER = '50';
    // The fee to be charged is too high
    string public constant CP_FEE_TOO_HIGH = '51';
    // The max fee to be paid is invalid
    string public constant CP_INVALID_MAX_FEE = '52';
    // The pool feature is not supported
    string public constant CP_NOT_SUPPORTED = '53';
    // The TCO2 decimals provided to retirement or detokenization requests are invalid
    string public constant TCO2_INVALID_DECIMALS = '54';
    // The TCO2 quantity in the batch is invalid
    string public constant COB_INVALID_QUANTITY = '55';
    // Splitting is required on detokenization/retirement finalization, but 2 new serial numbers
    // were not provided
    string public constant TCO2_MISSING_SERIALS = '56';
    // The score set for the ERC-1155 token in the pool is invalid
    string public constant INVALID_ERC1155_SCORE = '57';
    // The score of the ERC-1155 token in the pool is not set
    string public constant EMPTY_ERC155_SCORE = '58';
    // The underlying decimals are too high for the pool
    string public constant UNDERLYING_DECIMALS_TOO_HIGH = '59';
    // The provided supply cap is invalid and should match the underlying token decimals
    // eg, for an ERC-1155 token whose smallest denomination is tonnes, the pool supply
    // cap should not include decimals of lower fidelity than tonnes
    string public constant INVALID_SUPPLY_CAP = '60';
}
","keccak256":"0x66df4b0ff5ba3a4368757d2b18a5f14d8adf6d671b2ea54cb8bbe8e5ec8f82aa","license":"UNLICENSED"},"contracts/libraries/PuroSerialNumbers.sol":{"content":"// SPDX-FileCopyrightText: 2024 Toucan Labs
//
// SPDX-License-Identifier: UNLICENSED

// If you encounter a vulnerability or an issue, please contact <security@toucan.earth> or visit security.toucan.earth
pragma solidity 0.8.14;

import {Strings} from './Strings.sol';

struct SerialNumber {
    // Fields relevant to serial numbers prior to the
    // Puro API migration.
    string startSerial;
    string endSerial;
    string serialType;
    // Fields relevant to serial numbers after the
    // Puro API migration.
    string issuanceId;
    // Fields relevant to serial numbers both before and
    // after the Puro API migration.
    uint256 rangeStart;
    uint256 rangeEnd;
}

library PuroSerialNumbers {
    using Strings for string;
    using Strings for uint256;

    // The size info of the serials before the migration to the new Puro API.
    // Example: 643002406555908610000000140219-643002406555908610000000140220
    // We need to maintain the serial numbers that were added on-chain
    // before the migration until we can update serials directly on-chain (LILA-7025).
    uint256 internal constant TYPE_SIZE = 18;
    uint256 internal constant NUMBER_SIZE = 12;
    uint256 internal constant SERIAL_SIZE = TYPE_SIZE * 2 + NUMBER_SIZE * 2 + 1;
    // The info of the serials that are submitted on-chain after the Puro API migration.
    // Example: 40980b1a-cff7-4c78-9cc5-a3a2f18c7e0d_1000-1001
    uint256 internal constant ISSUANCE_ID_SIZE = 36;

    /// @notice Parse a serial number range
    /// @param serialNumber The serial number string to parse
    /// @return The parsed serial number
    function parseSerialNumber(string memory serialNumber)
        internal
        pure
        returns (SerialNumber memory)
    {
        if (bytes(serialNumber).length == SERIAL_SIZE) {
            return parseSerialNumberV1(serialNumber);
        }
        return parseSerialNumberV2(serialNumber);
    }

    /// @notice Parse a serial number range that was created on-chain before the
    /// Puro API migration.
    /// @param serialNumber The serial number string to parse
    /// @return The parsed serial number
    function parseSerialNumberV1(string memory serialNumber)
        internal
        pure
        returns (SerialNumber memory)
    {
        if (serialNumber.count('-') != 1) {
            revert('v1: incorrect delimiter count');
        }

        // Split the serial number range into start and end.
        (string memory startSerial, string memory endSerial) = serialNumber
            .split('-');

        // Get the part of the serial that is related to the type
        // of the batch and validate that it is the same for both
        // serials.
        string memory startType = startSerial.slice(0, TYPE_SIZE);
        string memory endType = endSerial.slice(0, TYPE_SIZE);
        if (!startType.equals(endType)) {
            revert('Type mismatch');
        }

        // Get the part of the serial that is related to the amount,
        // convert to integer, and ensure start is less than end.
        uint256 rangeStart = startSerial
            .slice(TYPE_SIZE + 1, TYPE_SIZE + NUMBER_SIZE)
            .toInteger();
        uint256 rangeEnd = endSerial
            .slice(TYPE_SIZE + 1, TYPE_SIZE + NUMBER_SIZE)
            .toInteger();
        if (rangeStart > rangeEnd) {
            revert('Invalid range');
        }

        return
            SerialNumber({
                issuanceId: '',
                startSerial: startSerial,
                endSerial: endSerial,
                serialType: startType,
                rangeStart: rangeStart,
                rangeEnd: rangeEnd
            });
    }

    /// @notice Parse a serial number range that was created on-chain after the
    /// Puro API migration.
    /// @param serialNumber The serial number string to parse
    /// @return The parsed serial number
    function parseSerialNumberV2(string memory serialNumber)
        internal
        pure
        returns (SerialNumber memory)
    {
        if (serialNumber.count('_') != 1) {
            revert('v2: incorrect delimiter count');
        }

        // Split the serial number range into issuance id and the
        // actual range.
        (string memory issuanceId, string memory range) = serialNumber.split(
            '_'
        );

        // Validate the issuance id.
        if (!isValidIssuanceId(issuanceId)) {
            revert('Invalid issuance id');
        }

        // Validate the range.
        if (range.count('-') != 1) {
            revert('Incorrect delimiter count in range');
        }
        (string memory startString, string memory endString) = range.split('-');
        uint256 rangeStart = startString.toInteger();
        uint256 rangeEnd = endString.toInteger();
        if (rangeStart > rangeEnd) {
            revert('Invalid range');
        }

        return
            SerialNumber({
                startSerial: '',
                endSerial: '',
                serialType: '',
                issuanceId: issuanceId,
                rangeStart: rangeStart,
                rangeEnd: rangeEnd
            });
    }

    function isValidIssuanceId(string memory issuanceId)
        internal
        pure
        returns (bool)
    {
        // Basic UUID validation, far from complete.
        // Probably not worth it to have more than this
        // on-chain but we could definitely extend in the
        // future as long as we don't deploy on L1.
        return
            bytes(issuanceId).length == ISSUANCE_ID_SIZE &&
            issuanceId.count('-') == 4;
    }

    /// @notice Split a serial number range into two parts based on
    /// the given amount.
    /// @param serialNumber The serial number to split
    /// @param amount The amount to split by
    /// @return balancingSerialNumber remainingSerialNumber The serial
    /// numbers split from the original serial number.
    function splitSerialNumber(SerialNumber memory serialNumber, uint256 amount)
        internal
        pure
        returns (
            string memory balancingSerialNumber,
            string memory remainingSerialNumber
        )
    {
        if (bytes(serialNumber.issuanceId).length != 0) {
            return splitSerialNumberV2(serialNumber, amount);
        }
        return splitSerialNumberV1(serialNumber, amount);
    }

    /// @notice Split a serial number range into two parts based on
    /// the given amount. This is valid for splitting serial numbers
    /// that were created on-chain before the Puro API migration.
    /// @param serialNumber The serial number to split
    /// @param amount The amount to split by
    /// @return balancingSerialNumber remainingSerialNumber The serial
    /// numbers split from the original serial number.
    function splitSerialNumberV1(
        SerialNumber memory serialNumber,
        uint256 amount
    )
        internal
        pure
        returns (
            string memory balancingSerialNumber,
            string memory remainingSerialNumber
        )
    {
        if (amount == 0) {
            revert('Empty amount');
        }
        if (amount >= serialNumber.rangeEnd - serialNumber.rangeStart + 1) {
            revert('Cannot split');
        }

        // Determine intermediate serial numbers
        uint256 balancingEndNum = serialNumber.rangeStart + amount - 1;
        uint256 remainingStartNum = balancingEndNum + 1;

        // Pad amounts
        string memory paddedBalancingEnd = balancingEndNum.toString().pad(
            NUMBER_SIZE,
            '0'
        );
        string memory paddedRemainingStart = remainingStartNum.toString().pad(
            NUMBER_SIZE,
            '0'
        );
        string memory balancingEnd = string.concat(
            serialNumber.serialType,
            paddedBalancingEnd
        );
        string memory remainingStart = string.concat(
            serialNumber.serialType,
            paddedRemainingStart
        );

        // Create new serials
        return (
            string.concat(
                string.concat(serialNumber.startSerial, '-'),
                balancingEnd
            ),
            string.concat(
                string.concat(remainingStart, '-'),
                serialNumber.endSerial
            )
        );
    }

    /// @notice Split a serial number range into two parts based on
    /// the given amount. This is valid for splitting serial numbers
    /// that were created on-chain after the Puro API migration.
    /// @param serialNumber The serial number to split
    /// @param amount The amount to split by
    /// @return balancingSerialNumber remainingSerialNumber The serial
    /// numbers split from the original serial number.
    function splitSerialNumberV2(
        SerialNumber memory serialNumber,
        uint256 amount
    )
        internal
        pure
        returns (
            string memory balancingSerialNumber,
            string memory remainingSerialNumber
        )
    {
        if (amount == 0) {
            revert('Empty amount');
        }
        if (amount >= serialNumber.rangeEnd - serialNumber.rangeStart + 1) {
            revert('Cannot split');
        }

        // Determine intermediate serial numbers
        uint256 balancingEndNum = serialNumber.rangeStart + amount - 1;
        uint256 remainingStartNum = balancingEndNum + 1;

        // Create new serials
        return (
            string.concat(
                serialNumber.issuanceId,
                '_',
                serialNumber.rangeStart.toString(),
                '-',
                balancingEndNum.toString()
            ),
            string.concat(
                serialNumber.issuanceId,
                '_',
                remainingStartNum.toString(),
                '-',
                serialNumber.rangeEnd.toString()
            )
        );
    }
}
","keccak256":"0x0f1be412e8420d29f1b3691106eea7025a27cc6334baec36602499f11b3c56ab","license":"UNLICENSED"},"contracts/libraries/Strings.sol":{"content":"// SPDX-FileCopyrightText: 2023 Toucan Labs
//
// SPDX-License-Identifier: UNLICENSED

// If you encounter a vulnerability or an issue, please contact <security@toucan.earth> or visit security.toucan.earth
pragma solidity 0.8.14;

import '@openzeppelin/contracts-upgradeable/utils/StringsUpgradeable.sol';

library Strings {
    /// @notice Compare two strings
    /// @param a The string to compare
    /// @param b The string to compare to
    /// @return True if the strings are equal, false otherwise
    function equals(string memory a, string memory b)
        internal
        pure
        returns (bool)
    {
        return StringsUpgradeable.equal(a, b);
    }

    /// @notice Convert a string to an integer
    /// @param numString The string to convert
    /// @return The integer value of the string
    function toInteger(string memory numString)
        internal
        pure
        returns (uint256)
    {
        uint256 val = 0;
        bytes memory stringBytes = bytes(numString);
        uint256 stringBytesLen = stringBytes.length;
        for (uint256 i = 0; i < stringBytesLen; ++i) {
            uint256 exp = stringBytesLen - i;
            bytes1 ival = stringBytes[i];
            uint8 uval = uint8(ival);
            uint256 jval = uval - uint256(0x30);

            val += (uint256(jval) * (10**(exp - 1)));
        }
        return val;
    }

    /// @notice Convert an integer to a string
    /// @param value The integer to convert
    /// @return The string value of the integer
    function toString(uint256 value) internal pure returns (string memory) {
        return StringsUpgradeable.toString(value);
    }

    /// @notice Get a substring of a string
    /// @param text The string to get a substring from
    /// @param begin The start index of the substring
    /// @param end The end index of the substring
    /// @return The substring
    function slice(
        string memory text,
        uint256 begin,
        uint256 end
    ) internal pure returns (string memory) {
        uint256 length = end - begin;
        bytes memory a = new bytes(length);
        for (uint256 i = 0; i < length; ++i) {
            a[i] = bytes(text)[i + begin];
        }
        return string(a);
    }

    /// @notice Pad a string with a character
    /// @param text The string to pad
    /// @param length The length to pad to
    /// @param padChar The character to pad with
    /// @return The padded string
    function pad(
        string memory text,
        uint256 length,
        string memory padChar
    ) internal pure returns (string memory) {
        uint256 textLen = bytes(text).length;
        require(bytes(padChar).length == 1, 'Invalid padChar length');
        require(length >= textLen, 'Invalid text length');

        for (uint256 i = textLen; i < length; ++i) {
            text = string.concat(padChar, text);
        }

        return text;
    }

    /// @notice Count the occurrences of a character in a string
    /// @param text The string to count occurrences of char in
    /// @param char The character to count
    /// @return nums The number of occurrences
    function count(string memory text, string memory char)
        internal
        pure
        returns (uint256 nums)
    {
        require(bytes(char).length == 1, 'Invalid char length');
        bytes1 c = bytes(char)[0];

        uint256 textLen = bytes(text).length;
        for (uint256 i = 0; i < textLen; ++i) {
            if (bytes(text)[i] == c) {
                ++nums;
            }
        }
    }

    /// @notice Split a string into two parts. The first occurrence of the delimiter
    /// is used to split the string into first and last.
    /// @param text The string to split
    /// @param delimiter The character to split on
    /// @return first last The two parts of the string
    function split(string memory text, string memory delimiter)
        internal
        pure
        returns (string memory first, string memory last)
    {
        require(bytes(delimiter).length == 1, 'Invalid delimiter length');
        bytes1 d = bytes(delimiter)[0];

        uint256 textLen = bytes(text).length;
        for (uint256 i = 0; i < textLen; ++i) {
            if (bytes(text)[i] == d) {
                first = slice(text, 0, i);
                last = slice(text, i + 1, textLen);
                return (first, last);
            }
        }
    }
}
